import React, { useState, useEffect, useMemo } from "react";
import { MessageCircle, X, Download, Upload, FileText, Bot, User } from "lucide-react";

const SECTIONS = {
  education: /\b(education|qualifications|degree|university|college)\b/i,
  skills: /\b(skills|technical skills|key skills|competencies)\b/i,
  experience: /\b(experience|work experience|employment|career)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?|licensed?)\b/i,
  contact: /\b(contact|email|phone|linkedin|github|address)\b/i,
};

const TEMPLATES = {
  modern: { name: "Modern", headerBg: "#2563eb", textColor: "#1f2937", font: "Georgia, serif" },
  minimal: { name: "Minimal", headerBg: "#374151", textColor: "#111827", font: "Arial, sans-serif" },
  corporate: { name: "Corporate", headerBg: "#1f2937", textColor: "#111827", font: "Times New Roman, serif" },
  creative: { name: "Creative", headerBg: "#7c3aed", textColor: "#1f2937", font: "Helvetica, sans-serif" }
};

const CHATBOT_RESPONSES = {
  greeting: "Hi! 👋 I'm your Resume Assistant. I can help you optimize for ATS systems, improve formatting, and enhance content. What would you like to know?",
  ats: "ATS optimization tips:\n• Use standard section headings\n• Include relevant keywords from job descriptions\n• Avoid tables, images, and complex formatting\n• Use bullet points and clear hierarchy\n• Save as .txt or simple .docx format",
  keywords: "Keyword strategies:\n• Mirror job description language\n• Use action verbs (managed, developed, implemented)\n• Include industry-specific terms\n• Quantify achievements with numbers\n• Balance hard and soft skills",
  format: "Formatting best practices:\n• Clean, consistent fonts\n• Adequate white space\n• Clear section headers\n• Reverse chronological order\n• One page for <10 years experience",
  sections: "Essential sections:\n• Contact Information\n• Professional Summary\n• Work Experience\n• Skills\n• Education\n\nOptional: Projects, Certifications, Awards",
  improve: "Quick improvements:\n• Add quantified achievements\n• Use strong action verbs\n• Tailor keywords to job postings\n• Keep formatting simple\n• Proofread carefully",
  default: "I can help with:\n• ATS optimization\n• Keyword suggestions\n• Formatting tips\n• Section recommendations\n• Content improvement\n\nWhat specific area interests you?"
};

const DEFAULT_MESSAGES = [
  { type: "bot", message: CHATBOT_RESPONSES.greeting },
  { type: "bot", message: "💡 Quick tip: Upload your resume first, then I can give you personalized advice!" },
  { type: "bot", message: "Common questions:\n• How to beat ATS systems?\n• What keywords should I use?\n• How to format my resume?\n• What sections are essential?" }
];

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const [showChatbot, setShowChatbot] = useState(false);
  const [showResume, setShowResume] = useState(false);
  const [chatMessages, setChatMessages] = useState(DEFAULT_MESSAGES);
  const [userMessage, setUserMessage] = useState("");

  // Analysis logic
  useEffect(() => {
    if (resumeText.trim()) {
      const found = {};
      const missing = [];
      
      for (const [key, regex] of Object.entries(SECTIONS)) {
        if (regex.test(resumeText)) {
          found[key] = true;
        } else {
          missing.push(key);
        }
      }
      
      const atsScore = Math.max(0, 100 - missing.length * 15);
      setAnalysis({ found, missing, atsScore });
    }
  }, [resumeText]);

  // File handling
  const handleFile = (file) => {
    if (!file) return;
    if (file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => setResumeText(e.target.result);
      reader.readAsText(file);
    } else {
      alert("Please upload a .txt file only");
    }
  };

  // Feedback generation
  const feedback = useMemo(() => {
    if (!analysis) return [];
    
    const fb = [];
    if (analysis.atsScore < 50) {
      fb.push("🔴 Critical: Add missing sections immediately");
    } else if (analysis.atsScore < 80) {
      fb.push("🟡 Good foundation, optimize for better results");
    } else {
      fb.push("🟢 Excellent! Your resume is ATS-friendly");
    }
    
    if (analysis.missing.length) {
      fb.push(`Missing: ${analysis.missing.join(", ")}`);
    }
    
    return fb;
  }, [analysis]);

  // Resume preview/download
  const generateResumeHTML = () => {
    const template = TEMPLATES[selectedTemplate];
    return `
      <div style="font-family: ${template.font}; max-width: 800px; margin: 0 auto; padding: 40px; background: white; color: ${template.textColor};">
        <div style="background: ${template.headerBg}; color: white; padding: 30px; text-align: center; margin-bottom: 30px; border-radius: 10px;">
          <h1 style="margin: 0; font-size: 2.5rem;">Professional Resume</h1>
          <p style="margin: 10px 0 0 0; opacity: 0.9;">${template.name} Template</p>
        </div>
        <div style="white-space: pre-wrap; line-height: 1.8; font-size: 14px;">${resumeText}</div>
      </div>
    `;
  };

  const downloadResume = () => {
    const element = document.createElement('a');
    const file = new Blob([generateResumeHTML()], { type: 'text/html' });
    element.href = URL.createObjectURL(file);
    element.download = `resume-${selectedTemplate}.html`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  // Chatbot functionality
  const handleChatSubmit = () => {
    if (!userMessage.trim()) return;
    
    const newMessages = [...chatMessages, { type: "user", message: userMessage }];
    const msg = userMessage.toLowerCase();
    
    let botResponse = CHATBOT_RESPONSES.default;
    if (msg.includes("ats") || msg.includes("system")) botResponse = CHATBOT_RESPONSES.ats;
    else if (msg.includes("keyword")) botResponse = CHATBOT_RESPONSES.keywords;
    else if (msg.includes("format") || msg.includes("layout")) botResponse = CHATBOT_RESPONSES.format;
    else if (msg.includes("section")) botResponse = CHATBOT_RESPONSES.sections;
    else if (msg.includes("improve") || msg.includes("better")) botResponse = CHATBOT_RESPONSES.improve;
    else if (msg.includes("hello") || msg.includes("hi")) botResponse = CHATBOT_RESPONSES.greeting;
    
    newMessages.push({ type: "bot", message: botResponse });
    setChatMessages(newMessages);
    setUserMessage("");
  };

  const ScoreCircle = ({ score }) => (
    <div className="relative w-24 h-24">
      <svg className="w-24 h-24 transform -rotate-90">
        <circle cx="48" cy="48" r="40" stroke="#e5e7eb" strokeWidth="8" fill="transparent" />
        <circle 
          cx="48" cy="48" r="40" 
          stroke={score >= 80 ? "#10b981" : score >= 50 ? "#f59e0b" : "#ef4444"}
          strokeWidth="8" 
          fill="transparent"
          strokeDasharray={`${2.51 * score} 251.2`}
          className="transition-all duration-1000 ease-out"
        />
      </svg>
      <div className="absolute inset-0 flex items-center justify-center">
        <span className="text-2xl font-bold">{score}%</span>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-700">
      {/* Header */}
      <header className="bg-gradient-to-r from-blue-800 to-purple-800 text-white py-16 text-center">
        <h1 className="text-5xl font-bold mb-4">🚀 Resume Analyzer</h1>
        <p className="text-xl opacity-90">AI-Powered ATS Optimization</p>
      </header>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto p-6 space-y-8">
        {/* Upload Section */}
        <div className="bg-white/95 backdrop-blur rounded-2xl p-8 shadow-xl">
          <h2 className="text-2xl font-semibold mb-6 text-center flex items-center justify-center gap-2">
            <Upload className="w-6 h-6" />
            Upload Your Resume
          </h2>
          
          <div
            className="border-3 border-dashed border-gray-300 rounded-xl p-8 text-center bg-gray-50/50 hover:bg-gray-100/50 transition-colors cursor-pointer"
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => {
              e.preventDefault();
              handleFile(e.dataTransfer.files[0]);
            }}
          >
            <FileText className="w-16 h-16 mx-auto mb-4 text-gray-400" />
            <p className="text-lg mb-4">Drag & Drop your resume (.txt)</p>
            <input
              type="file"
              accept=".txt"
              onChange={(e) => handleFile(e.target.files[0])}
              className="hidden"
              id="file-upload"
            />
            <label htmlFor="file-upload" className="bg-blue-500 text-white px-6 py-3 rounded-lg cursor-pointer hover:bg-blue-600 transition-colors">
              Choose File
            </label>
          </div>
        </div>

        {/* Template Selection */}
        {resumeText && (
          <div className="bg-white/95 backdrop-blur rounded-2xl p-8 shadow-xl">
            <h2 className="text-2xl font-semibold mb-6 text-center">🎨 Choose Template</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {Object.entries(TEMPLATES).map(([key, template]) => (
                <div
                  key={key}
                  className={`p-4 rounded-xl cursor-pointer transition-all ${
                    selectedTemplate === key 
                      ? 'bg-blue-100 border-2 border-blue-500 shadow-lg' 
                      : 'bg-gray-50 border border-gray-200 hover:shadow-md'
                  }`}
                  onClick={() => setSelectedTemplate(key)}
                >
                  <div 
                    className="w-full h-12 rounded mb-3"
                    style={{ backgroundColor: template.headerBg }}
                  ></div>
                  <h3 className="font-semibold text-center">{template.name}</h3>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Analysis Results */}
        {analysis && (
          <div className="bg-white/95 backdrop-blur rounded-2xl p-8 shadow-xl">
            <h2 className="text-2xl font-semibold mb-6 text-center">📊 Analysis Results</h2>
            
            {/* Score */}
            <div className="flex items-center justify-center gap-8 mb-8 p-6 bg-gradient-to-r from-blue-500 to-purple-600 rounded-xl text-white">
              <ScoreCircle score={analysis.atsScore} />
              <div>
                <h3 className="text-xl font-bold">ATS Compatibility</h3>
                <p className="opacity-90">
                  {analysis.atsScore >= 80 ? "Excellent!" : 
                   analysis.atsScore >= 50 ? "Good, but can improve" : "Needs work"}
                </p>
              </div>
            </div>

            {/* Sections */}
            <div className="grid md:grid-cols-2 gap-6 mb-6">
              <div className="bg-green-50 p-4 rounded-xl">
                <h3 className="font-semibold text-green-800 mb-3">
                  ✅ Found ({Object.keys(analysis.found).length})
                </h3>
                <div className="space-y-1">
                  {Object.keys(analysis.found).map((section) => (
                    <div key={section} className="text-green-700 capitalize">{section}</div>
                  ))}
                </div>
              </div>
              
              <div className="bg-red-50 p-4 rounded-xl">
                <h3 className="font-semibold text-red-800 mb-3">
                  ❌ Missing ({analysis.missing.length})
                </h3>
                <div className="space-y-1">
                  {analysis.missing.map((section) => (
                    <div key={section} className="text-red-700 capitalize">{section}</div>
                  ))}
                </div>
              </div>
            </div>

            {/* Feedback */}
            <div className="bg-blue-50 p-4 rounded-xl mb-6">
              <h3 className="font-semibold mb-3">💡 Recommendations</h3>
              <div className="space-y-2">
                {feedback.map((fb, i) => (
                  <div key={i} className="text-blue-800">{fb}</div>
                ))}
              </div>
            </div>

            {/* Actions */}
            <div className="flex gap-4 justify-center flex-wrap">
              <button
                onClick={() => setShowResume(true)}
                className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-colors flex items-center gap-2"
              >
                <FileText className="w-4 h-4" />
                Preview Resume
              </button>
              <button
                onClick={downloadResume}
                className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600 transition-colors flex items-center gap-2"
              >
                <Download className="w-4 h-4" />
                Download HTML
              </button>
            </div>
          </div>
        )}
      </main>

      {/* Resume Preview Modal */}
      {showResume && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl max-w-4xl max-h-[90vh] overflow-auto w-full">
            <div className="sticky top-0 bg-white border-b p-4 flex justify-between items-center">
              <h3 className="font-semibold">Resume Preview</h3>
              <button
                onClick={() => setShowResume(false)}
                className="p-2 hover:bg-gray-100 rounded-full"
              >
                <X className="w-5 h-5" />
              </button>
            </div>
            <div dangerouslySetInnerHTML={{ __html: generateResumeHTML() }} />
          </div>
        </div>
      )}

      {/* Chatbot Toggle */}
      <button
        onClick={() => setShowChatbot(true)}
        className="fixed bottom-6 right-6 bg-blue-500 text-white p-4 rounded-full shadow-lg hover:bg-blue-600 transition-colors z-40"
      >
        <MessageCircle className="w-6 h-6" />
      </button>

      {/* Chatbot */}
      {showChatbot && (
        <div className="fixed bottom-6 right-6 w-96 max-w-[calc(100vw-2rem)] bg-white rounded-xl shadow-2xl z-50 flex flex-col h-[500px]">
          <div className="bg-blue-500 text-white p-4 rounded-t-xl flex justify-between items-center">
            <div className="flex items-center gap-2">
              <Bot className="w-5 h-5" />
              <span className="font-semibold">Resume Assistant</span>
            </div>
            <button
              onClick={() => setShowChatbot(false)}
              className="p-1 hover:bg-blue-600 rounded"
            >
              <X className="w-4 h-4" />
            </button>
          </div>
          
          <div className="flex-1 overflow-y-auto p-4 space-y-3">
            {chatMessages.map((msg, i) => (
              <div
                key={i}
                className={`flex gap-2 ${msg.type === "user" ? "justify-end" : ""}`}
              >
                {msg.type === "bot" && <Bot className="w-6 h-6 text-blue-500 flex-shrink-0 mt-1" />}
                <div
                  className={`max-w-[80%] p-3 rounded-lg whitespace-pre-line text-sm ${
                    msg.type === "user"
                      ? "bg-blue-500 text-white"
                      : "bg-gray-100 text-gray-800"
                  }`}
                >
                  {msg.message}
                </div>
                {msg.type === "user" && <span className="w-6 h-6 flex-shrink-0 mt-1 text-lg">👤</span>}
              </div>
            ))}
          </div>
          
          <div className="p-4 border-t flex gap-2">
            <input
              value={userMessage}
              onChange={(e) => setUserMessage(e.target.value)}
              placeholder="Ask about resume optimization..."
              className="flex-1 p-2 border rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              onKeyPress={(e) => e.key === 'Enter' && handleChatSubmit()}
            />
            <button
              onClick={handleChatSubmit}
              className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors text-sm"
            >
              Send
            </button>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="bg-gray-800 text-white text-center py-8 mt-12">
        <p>© 2025 Dynamic Resume Analyzer - Boost Your Career Success</p>
      </footer>
    </div>
  );
}
