import React, { useState, useEffect, useMemo, useRef } from "react";

const SECTION = {
  education: /(education|qualifications|academic)/i,
  skills: /(skills|technical skills|key skills|competencies)/i,
  experience: /(experience|work experience|professional experience|employment)/i,
  projects: /(projects|personal projects|project experience)/i,
  certifications: /(certifications|certificates|licenses)/i,
  contact: /(contact|email|phone|linkedin|github|website)/i,
};

export default function App() {
  const heroImage = "/hero-resume.png";
  const [resumeText, setResumeText] = useState(() => localStorage.getItem("resumeText") || "");
  const [jobDescription, setJobDescription] = useState(() => localStorage.getItem("jobDescription") || "");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [modal, setModal] = useState({ show: false, message: "", confirm: null });
  const previewRef = useRef();

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
    localStorage.setItem("resumeText", resumeText);
  }, [resumeText, jobDescription]);

  useEffect(() => {
    localStorage.setItem("jobDescription", jobDescription);
  }, [jobDescription]);

  const handleFile = (file) => {
    if (!file) return;
    if (file.type !== "text/plain") {
      setModal({ show: true, message: "Only .txt resume uploads are supported for parsing. Use copy/paste for other formats.", confirm: null });
      return;
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) => text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTION)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }
    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = Array.from(new Set(splitWords(jobDescription)));
      const resumeWords = splitWords(resumeText);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }
    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) fb.push(`Missing sections: ${analysis.missing.join(", ")}`);
    if (atsScore !== null) {
      if (atsScore < 45) fb.push("ATS Match: Low. Add role-specific keywords to skills and experience bullets.");
      else if (atsScore < 75) fb.push("ATS Match: Moderate. Add a few targeted keywords from the job description.");
      else fb.push("ATS Match: Strong. Keyword alignment is good.");
    } else {
      fb.push("Paste a job description to compute ATS match.");
    }
    return fb;
  }, [analysis, atsScore]);

  const renderPreviewSections = () => {
    if (!resumeText.trim()) {
      return <div className="text-sm text-gray-400 italic">Paste your resume text to see a live preview.</div>;
    }
    const parts = resumeText.split(/\n{2,}/).map((p) => p.trim()).filter(Boolean);
    return parts.slice(0, 8).map((p, i) => (
      <section key={i} className="mb-4">
        <h4 className="text-sm font-semibold text-gray-800 mb-1">{p.split("\n")[0].slice(0, 70)}</h4>
        <p className="text-sm text-gray-600 leading-relaxed whitespace-pre-wrap">{p}</p>
      </section>
    ));
  };

  const downloadPdf = () => {
    const content = previewRef.current;
    if (!content) {
      setModal({ show: true, message: "Nothing to print. Paste resume text first.", confirm: null });
      return;
    }
    const title = "Resume Preview";
    const html = `
      <html>
        <head>
          <meta name="viewport" content="width=device-width,initial-scale=1" />
          <title>${title}</title>
          <style>
            body { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial; margin:24px; color:#111827; }
            h1 { font-size:20px; margin:0 0 12px 0; }
            .card { border:1px solid #e5e7eb; border-radius:8px; padding:14px; margin-bottom:12px; }
            .section-title { font-weight:700; margin-bottom:6px; }
            pre { white-space:pre-wrap; word-break:break-word; font-family:inherit; font-size:13px; }
          </style>
        </head>
        <body>
          <h1>Resume Preview</h1>
          <div class="card">
            <div class="section-title">Analysis</div>
            <div>Found: ${Object.keys(analysis?.found || {}).join(", ") || "—"}</div>
            <div>Missing: ${(analysis?.missing || []).join(", ") || "—"}</div>
            <div>ATS Score: ${atsScore !== null ? atsScore + "%" : "N/A"}</div>
          </div>
          <div class="card"><div class="section-title">Resume</div><pre>${escapeHtml(resumeText)}</pre></div>
          <div class="card"><div class="section-title">Job Description</div><pre>${escapeHtml(jobDescription)}</pre></div>
          <script>setTimeout(()=>window.print(),300)</script>
        </body>
      </html>
    `;
    const w = window.open("", "_blank", "width=900,height=800");
    if (!w) {
      setModal({ show: true, message: "Popup blocked. Allow popups for this site to download PDF.", confirm: null });
      return;
    }
    w.document.open();
    w.document.write(html);
    w.document.close();
  };

  const escapeHtml = (t) => (t ? t.replaceAll("&", "&amp;").replaceAll("<", "&lt;").replaceAll(">", "&gt;") : "");

  const clearAll = () => {
    setModal({
      show: true,
      message: "Clear resume and job description? This cannot be undone.",
      confirm: () => {
        setResumeText("");
        setJobDescription("");
        setAnalysis(null);
        setAtsScore(null);
        localStorage.removeItem("resumeText");
        localStorage.removeItem("jobDescription");
      },
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-900 via-slate-800 to-slate-700 text-gray-100 antialiased">
      <nav className="max-w-7xl mx-auto px-6 py-5 flex items-center justify-between">
        <div className="flex items-center gap-4">
          <div className="text-2xl font-extrabold tracking-tight">ResumePro</div>
          <div className="hidden md:flex gap-6 text-sm">
            <a className="hover:underline">Builders</a>
            <a className="hover:underline">Resumes</a>
            <a className="hover:underline">Cover Letters</a>
            <a className="hover:underline">Resources</a>
          </div>
        </div>
        <div className="flex items-center gap-3">
          <button onClick={() => setModal({ show: true, message: "Sign in is not implemented in this demo.", confirm: null })} className="px-4 py-2 border rounded-md text-sm bg-transparent hover:bg-slate-800">Login</button>
          <button onClick={() => setModal({ show: true, message: "Create account is not implemented in this demo.", confirm: null })} className="px-4 py-2 rounded-md bg-emerald-400 text-slate-900 font-semibold">Free Account</button>
        </div>
      </nav>

      <header className="max-w-7xl mx-auto px-6">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 items-center">
          <div className="lg:col-span-7">
            <h1 className="text-4xl md:text-5xl font-extrabold leading-tight tracking-tight mb-4 text-white">Free ATS Resume Checker</h1>
            <p className="text-lg text-slate-200 max-w-2xl mb-8">Get hired faster with an ATS-friendly resume. Our resume checker scans for key sections and keywords, then gives actionable suggestions to improve your match.</p>

            <div className="rounded-xl border-2 border-dashed border-slate-600 bg-slate-800/60 p-6 max-w-2xl">
              <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
                <div className="text-left">
                  <div className="text-lg font-semibold text-white">Check your score</div>
                  <div className="text-sm text-slate-300 mt-1">Supports: TXT (parsing). Use copy/paste for other formats.</div>
                </div>
                <div className="flex gap-3">
                  <label className="inline-block">
                    <input type="file" accept=".txt" onChange={(e) => handleFile(e.target.files[0])} className="hidden" />
                    <div className="px-5 py-2 rounded-md bg-emerald-400 text-slate-900 font-semibold cursor-pointer">Upload</div>
                  </label>
                  <button onClick={() => setModal({ show: true, message: "Drop your resume here or paste it in the editor below.", confirm: null })} className="px-5 py-2 rounded-md border border-slate-600 text-sm">How it works</button>
                </div>
              </div>
              <div className="mt-4 text-sm text-slate-300">Drag & drop your resume file onto the area below or paste your text in the editor.</div>
            </div>

            <div
              onDragEnter={() => setDragActive(true)}
              onDragLeave={() => setDragActive(false)}
              onDragOver={(e) => e.preventDefault()}
              onDrop={handleDrop}
              className={`mt-6 p-6 rounded-lg ${dragActive ? "bg-emerald-50/10 border-emerald-300" : "bg-white/5 border-slate-600/60"} border transition`}
            >
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <textarea
                    value={resumeText}
                    onChange={(e) => setResumeText(e.target.value)}
                    placeholder="Paste your resume text here..."
                    className="w-full min-h-[180px] p-4 rounded-md bg-transparent border border-slate-600 text-sm text-white placeholder-slate-300 focus:outline-none focus:ring-2 focus:ring-emerald-400"
                  />
                </div>
                <div>
                  <textarea
                    value={jobDescription}
                    onChange={(e) => setJobDescription(e.target.value)}
                    placeholder="Paste the job description for ATS matching..."
                    className="w-full min-h-[180px] p-4 rounded-md bg-transparent border border-slate-600 text-sm text-white placeholder-slate-300 focus:outline-none focus:ring-2 focus:ring-emerald-400"
                  />
                  <div className="flex justify-end mt-3 gap-3">
                    <button onClick={analyzeResume} className="px-4 py-2 rounded-md bg-indigo-600 text-white">Analyze</button>
                    <button onClick={downloadPdf} className="px-4 py-2 rounded-md border border-slate-600">Save as PDF</button>
                  </div>
                </div>
              </div>
            </div>

            <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="rounded-lg bg-white/5 p-4">
                <div className="text-xs text-slate-300">ATS Score</div>
                <div className="mt-2 text-lg font-semibold text-white">{atsScore !== null ? `${atsScore}%` : "—"}</div>
                <div className="w-full bg-slate-600 rounded-full h-3 mt-3">
                  <div
                    style={{ width: `${atsScore || 0}%` }}
                    className={`h-3 rounded-full ${atsScore === null ? "bg-slate-400" : atsScore < 50 ? "bg-rose-500" : atsScore < 80 ? "bg-yellow-500" : "bg-emerald-400"} transition-all`}
                  />
                </div>
              </div>
              <div className="rounded-lg bg-white/5 p-4">
                <div className="text-xs text-slate-300">Found Sections</div>
                <div className="mt-2 text-sm text-white">{analysis ? Object.keys(analysis.found).length : "—"}</div>
              </div>
              <div className="rounded-lg bg-white/5 p-4">
                <div className="text-xs text-slate-300">Missing Sections</div>
                <div className="mt-2 text-sm text-white">{analysis ? analysis.missing.length : "—"}</div>
              </div>
            </div>
          </div>

          <div className="lg:col-span-5 lg:col-start-8 relative hidden lg:block">
            <div className="relative w-full h-full flex items-center justify-center">
              <div className="absolute -right-16 top-8 w-[420px] h-[520px] rounded-2xl bg-white shadow-2xl p-6 overflow-hidden">
                <div className="h-full flex flex-col">
                  <div className="flex items-center justify-between">
                    <div className="text-sm font-semibold text-slate-800">Resume Preview</div>
                    <div className="text-xs text-slate-500">Preview</div>
                  </div>
                  <div className="mt-4 flex-1 overflow-auto">
                    <div className="text-sm text-slate-700">
                      {resumeText ? resumeText.slice(0, 1500) : "Your resume content appears here when you paste or upload."}
                    </div>
                  </div>
                  <div className="mt-4">
                    <div className="rounded-md bg-slate-50 p-3 text-sm text-slate-800">Detected: {analysis ? Object.keys(analysis.found).join(", ") || "—" : "—"}</div>
                  </div>
                </div>
              </div>
              <div className="w-[520px] h-[520px] rounded-full bg-slate-800/60 flex items-center justify-center">
                <img src={heroImage} alt="hero" className="max-w-[80%] max-h-[80%] object-contain opacity-95" />
              </div>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-10">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2 rounded-lg bg-white/5 p-6">
            <div ref={previewRef} className="prose prose-invert max-w-none">
              <h2 className="text-2xl text-white font-semibold">Live Preview & Detailed Analysis</h2>
              {renderPreviewSections()}
            </div>
          </div>
          <aside className="rounded-lg bg-white/5 p-6">
            <h3 className="text-lg font-semibold text-white mb-3">Suggestions</h3>
            <ul className="text-sm text-slate-200 space-y-2">
              {feedback.map((f, i) => <li key={i}>{f}</li>)}
            </ul>
            <div className="mt-6">
              <button onClick={clearAll} className="w-full px-4 py-2 rounded-md bg-rose-500 text-white">Clear All</button>
            </div>
          </aside>
        </div>
      </main>

      <footer className="max-w-7xl mx-auto px-6 py-6 text-sm text-slate-300">
        Built with React and Tailwind. Data stays in your browser.
      </footer>

      {modal.show && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg w-full max-w-md p-6">
            <div className="text-sm text-gray-800">{modal.message}</div>
            <div className="flex justify-end gap-3 mt-6">
              {modal.confirm && <button onClick={() => { modal.confirm(); setModal({ show: false, message: "", confirm: null }); }} className="px-4 py-2 bg-indigo-600 text-white rounded-md">Yes</button>}
              <button onClick={() => setModal({ show: false, message: "", confirm: null })} className="px-4 py-2 border rounded-md">Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
