import React, { useEffect, useMemo, useRef, useState } from "react";

const SECTIONS = {
  contact: /\b(contact|email|phone|linkedin|github|address)\b/i,
  summary: /\b(summary|objective|profile)\b/i,
  experience: /\b(experience|work experience|employment|career)\b/i,
  skills: /\b(skills|technical skills|competencies|technologies)\b/i,
  education: /\b(education|degree|university|college)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?|license|licensed?)\b/i,
};

const TEMPLATES = {
  modern: {
    name: "Modern Professional",
    className: "theme-modern",
    fontStack: "ui-sans-serif, system-ui, Segoe UI, Arial",
  },
  minimal: {
    name: "Minimal Clean",
    className: "theme-minimal",
    fontStack: "Arial, Helvetica, sans-serif",
  },
  creative: {
    name: "Creative Bold",
    className: "theme-creative",
    fontStack: "Helvetica, Arial, sans-serif",
  },
};

const STOPWORDS = new Set([
  "and","the","with","from","that","this","your","their","our","for","into","able","will",
  "shall","must","have","has","had","are","was","were","you","they","them","over","under",
  "about","above","below","not","only","but","also","more","than","such","etc","using",
  "use","used","strong","good","great","work","role","team","skills","requirements",
  "responsibilities","job","description","looking","plus","preferred","required",
  "experience","years","year","developer","engineer","data",
]);

function extractKeywords(text) {
  return [...new Set(text.toLowerCase().split(/[^a-z0-9+#.]/i)
    .map(w => w.trim())
    .filter(w => w.length > 2 && !STOPWORDS.has(w)))];
}

function firstNonEmptyLine(s) {
  return (s || "").split(/\r?\n/).map(x => x.trim()).find(x => x.length > 0) || "Candidate Name";
}

function escapeHTML(s) {
  return s.replaceAll("&","&amp;").replaceAll("<","&lt;").replaceAll(">","&gt;");
}

function plainToHTML(resumeText) {
  const lines = (resumeText || "").split(/\r?\n/);
  return lines.map(line => {
    const trimmed = line.trim();
    if (!trimmed) return "";
    const isHeading = Object.values(SECTIONS).some(r => r.test(trimmed));
    const isBullet = trimmed.match(/^[-*\‚Ä¢\d.].*$/);
    if (isHeading) return `<h2>${escapeHTML(trimmed)}</h2>`;
    if (isBullet) return `<ul><li>${escapeHTML(trimmed.replace(/^[-*\‚Ä¢\d.]\s*/, ""))}</li></ul>`;
    return `<p>${escapeHTML(trimmed)}</p>`;
  }).join("\n");
}

function calculateATSScore(resumeText, jobDescription) {
  const found = {};
  const missing = [];
  Object.entries(SECTIONS).forEach(([k, rgx]) => {
    if (rgx.test(resumeText)) found[k] = true;
    else missing.push(k);
  });

  const jdKeys = extractKeywords(jobDescription);
  const resumeLower = resumeText.toLowerCase();
  const matched = jdKeys.filter(k => resumeLower.includes(k));
  const keywordMatchRatio = jdKeys.length ? matched.length / jdKeys.length : 1;
  const keywordScore = Math.round(keywordMatchRatio * 40);
  const coreSections = ["contact", "summary", "experience", "skills", "education"];
  const coreMissingCount = coreSections.filter(k => missing.includes(k)).length;
  const structureScore = Math.max(0, 60 - coreMissingCount * 12);
  const atsScore = Math.max(0, Math.min(100, structureScore + keywordScore));
  const missingKeys = jdKeys.filter(k => !resumeLower.includes(k));

  return { found, missing, matched, missingKeys, atsScore, uniqueJD: jdKeys.length };
}

export default function App({ userEmail, passwordHash }) {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const [analysis, setAnalysis] = useState(null);
  const [fileError, setFileError] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const previewRef = useRef(null);
  const fileInputRef = useRef(null);
  const [libsReady, setLibsReady] = useState(false);

  // ‚úÖ Dynamically load libraries
  useEffect(() => {
    const loadLib = (src) => new Promise((resolve, reject) => {
      const s = document.createElement("script");
      s.src = src;
      s.onload = resolve;
      s.onerror = reject;
      document.body.appendChild(s);
    });

    Promise.all([
      loadLib("https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js"),
      loadLib("https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js")
    ])
      .then(() => {
        console.log("‚úÖ Libraries loaded");
        setLibsReady(true);
      })
      .catch((e) => console.error("Library load error", e));
  }, []);

  const handleFile = (file) => {
    setFileError(null);
    if (!file) return;
    if (file.size > 2 * 1024 * 1024) {
      setFileError("File too large (Max 2MB).");
      setResumeText("");
      return;
    }

    if (file.type === "application/pdf") {
      setFileError("‚ö†Ô∏è PDF selected. Use .txt for accurate analysis. Using dummy content.");
      setResumeText("Candidate Name\nContact Information\nExperience\nSkills: React, JS\nEducation");
      return;
    }

    if (file.type !== "text/plain") {
      setFileError("Upload .txt or .pdf only.");
      setResumeText("");
      return;
    }

    const reader = new FileReader();
    reader.onload = e => setResumeText(String(e.target.result || ""));
    reader.readAsText(file);
  };

  useEffect(() => {
    if (!resumeText) return setAnalysis(null);
    setAnalysis(calculateATSScore(resumeText, jobDescription));
  }, [resumeText, jobDescription]);

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const list = [];
    if (analysis.missing.length > 0)
      list.push(`‚ùå Missing sections: ${analysis.missing.join(", ")}.`);
    else list.push(`‚úÖ Structure complete.`);

    const matchPct = analysis.uniqueJD
      ? Math.round((analysis.matched.length / analysis.uniqueJD) * 100)
      : 100;

    if (analysis.uniqueJD === 0)
      list.push(`üîé No JD provided.`);
    else if (matchPct < 40)
      list.push(`üî¥ Low Keyword Match (${matchPct}%)`);
    else if (matchPct < 70)
      list.push(`üü° Moderate Match (${matchPct}%)`);
    else list.push(`üü¢ Strong Match (${matchPct}%)`);

    list.push(`üìä ATS Score: ${analysis.atsScore}%`);
    return list;
  }, [analysis]);

  const candidateName = useMemo(() => firstNonEmptyLine(resumeText), [resumeText]);
  const htmlBody = useMemo(() => plainToHTML(resumeText), [resumeText]);

  const APPS_SCRIPT_URL = "https://script.google.com/macros/s/AKfycbzID5vI2-R5DvJNYnhvMpSBkNOlP4JxBOCKJC-rnVBHZc9AqKy2tUUZ7FZq5HblQMrl/exec";

  const downloadPDF = async () => {
    if (!libsReady) {
      alert("Libraries still loading. Try again in a moment.");
      return;
    }

    const { jsPDF } = window.jspdf || {};
    if (!jsPDF || !window.html2canvas) {
      alert("Libraries still loading. Try again in a moment.");
      return;
    }

    setIsUploading(true);
    const node = previewRef.current;
    const originalClass = node.className;
    node.className = `resume-sheet ${TEMPLATES[selectedTemplate].className}`;
    node.style.fontFamily = TEMPLATES[selectedTemplate].fontStack;

    try {
      const canvas = await window.html2canvas(node, { scale: 3, backgroundColor: "#fff", useCORS: true });
      const pdf = new jsPDF("p", "pt", "a4");
      const img = canvas.toDataURL("image/png");
      const pageW = pdf.internal.pageSize.getWidth();
      const pageH = pdf.internal.pageSize.getHeight();
      const imgH = (canvas.height * pageW) / canvas.width;
      let y = 0, remaining = imgH;

      while (remaining > 0) {
        pdf.addImage(img, "PNG", 0, -y, pageW, imgH);
        remaining -= pageH;
        y += pageH;
        if (remaining > 0) pdf.addPage();
      }

      const fileName = `${candidateName.replace(/\s/g, "_")}_Resume.pdf`;
      pdf.save(fileName);
      const pdfBlob = pdf.output("blob");
      const base64 = await blobToBase64(pdfBlob);

      const payload = new URLSearchParams({
        action: "store",
        email: userEmail,
        passwordHash,
        fileName,
        resumeFile: base64,
      });

      const response = await fetch(APPS_SCRIPT_URL, { method: "POST", body: payload });
      const contentType = response.headers.get("content-type");
      if (!contentType?.includes("application/json"))
        throw new Error("Server returned non-JSON response.");

      const result = await response.json();
      if (result.status === "success") {
        alert(`‚úÖ Resume uploaded!\nDrive link: ${result.link}`);
      } else {
        alert(`‚ùå Upload failed: ${result.message}`);
      }
    } catch (err) {
      alert(`‚ö†Ô∏è Error: ${err.message}`);
    } finally {
      setIsUploading(false);
      node.className = originalClass;
      node.style.fontFamily = "";
    }
  };

  function blobToBase64(blob) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result.split(",")[1]);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  }

  return (
    <section>
      <div className="grid-2">
        <div className="card">
          <h2>üìÑ Resume Upload</h2>
          <div className="resume-dropzone"
            onDrop={e => { e.preventDefault(); e.stopPropagation(); if (e.dataTransfer.files.length > 0) handleFile(e.dataTransfer.files[0]); }}
            onDragOver={e => { e.preventDefault(); e.stopPropagation(); }}
          >
            <p>Drag & Drop or Select a File (.txt, .pdf)</p>
            <input ref={fileInputRef} type="file" accept=".txt,application/pdf" style={{ display: "none" }}
              onChange={e => handleFile(e.target.files?.[0])} />
            <button onClick={() => fileInputRef.current.click()}>Select File</button>
            {fileError && <p className="hint file-error">{fileError}</p>}
          </div>
        </div>

        <div className="card">
          <h2>üìù Job Description</h2>
          <textarea className="text-input jd-input" placeholder="Paste JD here..." value={jobDescription} onChange={e => setJobDescription(e.target.value)} />
        </div>
      </div>

      {analysis && (
        <div className="card">
          <h2>üìä ATS Analysis (Score: {analysis.atsScore}%)</h2>
          <ul>{feedback.map((f, i) => <li key={i}>{f}</li>)}</ul>
          <button className="btn primary" onClick={downloadPDF} disabled={isUploading}>
            {isUploading ? "Uploading..." : "üìÑ Download & Upload PDF"}
          </button>
        </div>
      )}

      {resumeText && (
        <div ref={previewRef} className={`resume-sheet ${TEMPLATES[selectedTemplate].className}`}
          style={{ fontFamily: TEMPLATES[selectedTemplate].fontStack }}>
          <div className="sheet-header">
            <div className="avatar">{candidateName.slice(0, 1).toUpperCase()}</div>
            <div className="headings">
              <h1 className="name">{candidateName}</h1>
              <div className="tagline">Professional Resume</div>
            </div>
          </div>
          <div className="sheet-body" dangerouslySetInnerHTML={{ __html: htmlBody }} />
        </div>
      )}
    </section>
  );
}
