import React, { useState } from "react";

// !!! USING YOUR LATEST DEPLOYED GOOGLE APPS SCRIPT WEB APP URL !!!
const APPS_SCRIPT_URL = "https://script.google.com/macros/s/AKfycbwt3y2Qq_nWTqwrlZykkamyOe3QsQERFmaOK2wwhqg504QSQ2bx7lv_G7bjAMeWzvbg/exec";

/**
 * Authentication Component for Login/Register using Google Apps Script as backend.
 */
export default function Auth({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState(null);

  const action = isLogin ? "login" : "register";
  const formTitle = isLogin ? "Login" : "Register";

  // Simple fetch function without complex retry logic to reduce potential failure points
  const handleApiCall = async (url, payload) => {
    // Send the request
    const response = await fetch(url, {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload)
    });

    // Check for HTTP errors (like 404, 500, etc.)
    if (!response.ok) {
        // Attempt to read the error body for better insight
        let errorText = await response.text();
        // If it's a silent GAS error, it often returns an HTML page
        if (errorText.includes("<title>Error</title>")) {
             errorText = "Apps Script deployment error. Check execution logs!";
        } else if (errorText.length > 100) {
             errorText = "Generic HTTP error received. Status: " + response.status;
        }
        throw new Error(`HTTP Status ${response.status}: ${errorText}`);
    }

    return response.json();
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
    setIsLoading(true);

    const payload = {
        action: action,
        email: email,
        password: password
    };

    try {
      const result = await handleApiCall(APPS_SCRIPT_URL, payload);

      if (result.success) {
        setMessage({ type: 'success', text: result.message });
        setTimeout(() => onLogin(email), 1500); 
      } else {
        setMessage({ type: 'error', text: result.message || `An unknown server error occurred during ${action}.` });
      }

    } catch (error) {
      console.error(`Fetch Error:`, error);
      // Display the specific error message to the user
      setMessage({ type: 'error', text: `Connection Error: ${error.message}` });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="card auth-card">
        <h2 className="section-title center">{formTitle} to use the App</h2>
        
        {/* API Message Display */}
        {message && (
          <div className={`api-message ${message.type === 'error' ? 'error' : 'success'}`}>
            {message.text}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <input
            className="text-input"
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={isLoading}
          />
          <input
            className="text-input"
            type="password"
            placeholder="Password (Must be 6+ chars)"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            minLength="6"
            required
            disabled={isLoading}
          />
          <button type="submit" className="btn primary full-width" disabled={isLoading}>
            {isLoading ? "Processing..." : formTitle}
          </button>
        </form>
        
        <div className="switch-auth center">
          {isLogin ? "Need an account?" : "Already have an account?"}
          <button 
            className="link-button" 
            onClick={() => {
                setIsLogin(!isLogin);
                setMessage(null);
            }} 
            type="button"
            disabled={isLoading}
          >
            {isLogin ? "Register" : "Login"}
          </button>
        </div>
        <p className="hint center mt-1">
          <small>
            Authentication uses Google Apps Script as the backend API.
          </small>
        </p>
      </div>
    </div>
  );
}
