import React, { useState, useEffect, useMemo } from "react";

const SECTION = {
  education: /(education|qualifications)/i,
  skills: /(skills|technical skills|key skills)/i,
  experience: /(experience|work experience )/i,
  projects: /(projects)/i,
  certifications: /(certifications)/i,
  contact: /(contact|email|phone|linkedin|github)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
  }, [resumeText, jobDescription]);

  const handleFile = (file) => {
    if (!file || file.type !== "text/plain") {
      window.alert("Only .txt resumes supported");
      return;
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) => text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTION)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }
    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = splitWords(jobDescription);
      const resumeWords = splitWords(resumeText);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }
    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}.`);
    }
    if (atsScore !== null) {
      if (atsScore < 50) fb.push("Low score. Add more keywords/skills.");
      else if (atsScore < 80) fb.push("Decent score, but can improve.");
      else fb.push("Excellent match!");
    }
    return fb;
  }, [analysis, atsScore]);

  const downloadPDF = () => {
    const content = document.getElementById("results-section").innerHTML;
    const win = window.open("", "_blank");
    win.document.write(`
      <html>
        <head>
          <title>Resume Analysis Report</title>
          <style>
            body { font-family: Arial, sans-serif; padding: 20px; }
            h2 { color: #0f172a; }
            .score { font-size: 20px; font-weight: bold; }
          </style>
        </head>
        <body>${content}</body>
      </html>
    `);
    win.document.close();
    win.print();
  };

  return (
    <div style={styles.app}>
      <header style={styles.hero}>
        <h1 style={styles.heroTitle}>Dynamic Resume Analyzer</h1>
        <p style={styles.heroText}>
          Upload your resume and job description to check missing sections and
          get an ATS score instantly.
        </p>
      </header>

      <main style={styles.container}>
        <section style={styles.section}>
          <h2>Upload Resume</h2>
          <input
            type="file"
            accept=".txt"
            onChange={(e) => handleFile(e.target.files[0])}
            style={styles.fileInput}
          />
          <div
            style={styles.dropzone}
            onDragOver={(e) => e.preventDefault()}
            onDrop={handleDrop}
          >
            Drag & Drop your resume here
          </div>
        </section>

        <section style={styles.section}>
          <h2>Job Description</h2>
          <textarea
            placeholder="Paste the job description here..."
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
            style={styles.textarea}
          />
        </section>

        {analysis && (
          <section id="results-section" style={styles.results}>
            <h2>Results</h2>
            <div style={styles.cards}>
              <div style={styles.card}>
                <h3>Found Sections</h3>
                <ul>
                  {Object.keys(analysis.found).map((s) => (
                    <li key={s}>{s}</li>
                  ))}
                </ul>
              </div>
              <div style={styles.card}>
                <h3>Missing Sections</h3>
                <ul>
                  {analysis.missing.map((s) => (
                    <li key={s}>{s}</li>
                  ))}
                </ul>
              </div>
              <div style={{ ...styles.card, ...styles.scoreCard }}>
                <h3>ATS Score</h3>
                <p style={{ fontSize: "2rem", fontWeight: "bold" }}>
                  {atsScore !== null ? `${atsScore}%` : "Paste JD first"}
                </p>
              </div>
            </div>
            <div>
              <h3>Feedback</h3>
              <ul>
                {feedback.map((f, i) => (
                  <li key={i}>{f}</li>
                ))}
              </ul>
            </div>
            <button style={styles.button} onClick={downloadPDF}>
              Download PDF
            </button>
          </section>
        )}
      </main>
    </div>
  );
}

const styles = {
  app: { textAlign: "center", background: "#f8f9fc", minHeight: "100vh" },
  hero: {
    background: "linear-gradient(135deg, #0f172a, #1e293b)",
    color: "white",
    padding: "4rem 2rem",
  },
  heroTitle: { fontSize: "2.5rem", marginBottom: "1rem" },
  heroText: { fontSize: "1.1rem", maxWidth: "600px", margin: "0 auto" },
  container: { maxWidth: "1000px", margin: "2rem auto", padding: "0 1rem" },
  section: { marginBottom: "3rem", textAlign: "left" },
  fileInput: { display: "block", marginBottom: "1rem" },
  dropzone: {
    border: "2px dashed #64748b",
    borderRadius: "10px",
    padding: "2rem",
    textAlign: "center",
    color: "#64748b",
    transition: "all 0.3s ease",
  },
  textarea: {
    width: "100%",
    minHeight: "200px",
    padding: "1rem",
    borderRadius: "8px",
    border: "1px solid #cbd5e1",
    fontSize: "1rem",
  },
  results: {
    background: "white",
    borderRadius: "12px",
    padding: "2rem",
    boxShadow: "0 8px 20px rgba(0,0,0,0.05)",
  },
  cards: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: "1.5rem",
    marginBottom: "2rem",
  },
  card: {
    background: "#f8fafc",
    padding: "1.5rem",
    borderRadius: "10px",
    border: "1px solid #e2e8f0",
    textAlign: "center",
  },
  scoreCard: { background: "#0f172a", color: "white" },
  button: {
    background: "#0f172a",
    color: "white",
    border: "none",
    padding: "0.8rem 1.5rem",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "1rem",
    marginTop: "1rem",
  },
};
