// src/App.tsx
import { useState } from "react";
import { auth, db } from "./firebase";
import { signInWithPopup, GoogleAuthProvider } from "firebase/auth";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";

const sectionRegex: Record<string, RegExp> = {
  contact: /(contact|details|information)/i,
  education: /(education|qualification|academics)/i,
  experience: /(experience|work|employment|career)/i,
  skills: /(skills|technologies|tools)/i,
};

function analyzeResume(text: string) {
  const sections: string[] = [];
  Object.entries(sectionRegex).forEach(([key, regex]) => {
    if (regex.test(text)) sections.push(key);
  });

  const missing = Object.keys(sectionRegex).filter(
    (key) => !sections.includes(key)
  );

  const score = Math.round(
    (sections.length / Object.keys(sectionRegex).length) * 100
  );

  return { sections, missing, score };
}

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [analysis, setAnalysis] = useState<any>(null);
  const [user, setUser] = useState<any>(null);

  const handleLogin = async () => {
    const provider = new GoogleAuthProvider();
    const result = await signInWithPopup(auth, provider);
    setUser(result.user);
  };

  const handleAnalyze = async () => {
    if (!resumeText.trim()) return;
    const result = analyzeResume(resumeText);
    setAnalysis(result);

    if (user) {
      await addDoc(collection(db, "analyses"), {
        uid: user.uid,
        resumeText,
        ...result,
        createdAt: serverTimestamp(),
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-4">Dynamic Resume Analyser</h1>

      {!user ? (
        <button
          onClick={handleLogin}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg shadow"
        >
          Sign in with Google
        </button>
      ) : (
        <p className="mb-4">Welcome, {user.displayName}</p>
      )}

      <textarea
        className="w-full max-w-2xl p-3 border rounded-lg h-64 mb-4"
        placeholder="Paste your resume text here..."
        value={resumeText}
        onChange={(e) => setResumeText(e.target.value)}
      />

      <button
        onClick={handleAnalyze}
        className="px-4 py-2 bg-green-600 text-white rounded-lg shadow"
      >
        Analyze Resume
      </button>

      {analysis && (
        <div className="mt-6 w-full max-w-2xl p-4 bg-white rounded-xl shadow">
          <h2 className="text-xl font-semibold mb-2">Analysis Result</h2>
          <p className="mb-2">Score: {analysis.score}/100</p>

          <div>
            <p className="font-medium">Detected Sections:</p>
            <ul className="list-disc ml-6">
              {analysis.sections.map((s: string) => (
                <li key={s}>{s}</li>
              ))}
            </ul>
          </div>

          <div className="mt-2">
            <p className="font-medium text-red-600">Missing Sections:</p>
            <ul className="list-disc ml-6">
              {analysis.missing.map((m: string) => (
                <li key={m}>{m}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
