import React, { useState, useEffect, useMemo, useRef } from "react";

/**
 * Dynamic Resume Analyzer — visually upgraded + PDF download + autosave
 *
 * Requirements:
 * - Tailwind CSS must be available in the project
 * - No external libs used
 *
 * Usage:
 * - Paste into a React file (e.g., src/App.jsx)
 * - Ensure your project has Tailwind configured
 */

const SECTION = {
  education: /(education|qualifications|academic)/i,
  skills: /(skills|technical skills|key skills|competencies)/i,
  experience: /(experience|work experience|professional experience|employment)/i,
  projects: /(projects|personal projects|project experience)/i,
  certifications: /(certifications|certificates|licenses)/i,
  contact: /(contact|email|phone|linkedin|github|website)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState(
    () => localStorage.getItem("resumeText") || ""
  );
  const [jobDescription, setJobDescription] = useState(
    () => localStorage.getItem("jobDescription") || ""
  );
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const previewRef = useRef();

  // Auto-analyze on content changes
  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
    // persist
    localStorage.setItem("resumeText", resumeText);
  }, [resumeText, jobDescription]);

  useEffect(() => {
    localStorage.setItem("jobDescription", jobDescription);
  }, [jobDescription]);

  // File handler: only .txt (per your original)
  const handleFile = (file) => {
    if (!file) return;
    if (file.type !== "text/plain") {
      // allow PDF? You asked for PDF download only, so keep upload to .txt
      return alert("Only .txt resume uploads are supported. Copy/paste works for others.");
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) =>
    text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTION)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }

    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = splitWords(jobDescription);
      const resumeWords = splitWords(resumeText);
      // deduplicate JD words to avoid overweighting repeated words
      const uniqueJd = Array.from(new Set(jdWords));
      const matches = uniqueJd.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / uniqueJd.length) * 100));
    }
    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}.`);
    } else {
      fb.push("All core sections detected — nice.");
    }
    if (atsScore !== null) {
      if (atsScore < 45) fb.push("ATS Match: Low. Add role-specific keywords & skills.");
      else if (atsScore < 75) fb.push("ATS Match: Moderate. Add a few targeted keywords.");
      else fb.push("ATS Match: Strong. Good keyword alignment.");
    } else {
      fb.push("Paste a Job Description to compute ATS match.");
    }
    return fb;
  }, [analysis, atsScore]);

  // Parse resume slightly for preview: naive split by headings or newlines. Keep simple.
  const renderPreviewSections = () => {
    if (!resumeText.trim()) {
      return (
        <div className="text-sm text-gray-500 italic">
          Paste your resume text to see a live preview here.
        </div>
      );
    }
    // Split by two or more newlines as sections
    const parts = resumeText.split(/\n{2,}/).map((p) => p.trim()).filter(Boolean);
    return parts.slice(0, 8).map((p, i) => (
      <section key={i} className="mb-4">
        <h4 className="text-sm font-semibold text-gray-700 mb-1">
          {p.split("\n")[0].slice(0, 60)}
        </h4>
        <p className="text-sm text-gray-600 leading-relaxed whitespace-pre-wrap">
          {p}
        </p>
      </section>
    ));
  };

  // Print / Save as PDF
  const downloadPdf = () => {
    // Create print-friendly HTML content using the previewRef DOM
    const content = previewRef.current;
    if (!content) {
      alert("Nothing to print. Paste resume text to preview first.");
      return;
    }
    // Build a standalone HTML string with inline styles (tailwind classes won't carry over),
    // so we'll include minimal inline styles to preserve layout.
    const title = "Resume — Dynamic Resume Analyzer";
    const html = `
      <html>
      <head>
        <title>${title}</title>
        <meta name="viewport" content="width=device-width, initial-scale=1"/>
        <style>
          /* Basic printable styles (kept minimal so browsers render well) */
          body { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial; margin: 24px; color: #111827; }
          .header { display:flex; justify-content:space-between; align-items:center; margin-bottom:18px; }
          h1 { font-size:20px; margin:0; }
          .card { border: 1px solid #e6e6e6; border-radius:8px; padding:14px; margin-bottom:12px; }
          .section-title { font-weight:700; margin-bottom:6px; }
          pre { white-space:pre-wrap; word-break:break-word; font-family:inherit; font-size:13px; }
          .meta { font-size:13px; color:#374151; }
        </style>
      </head>
      <body>
        <div class="header">
          <h1>Resume Preview</h1>
          <div class="meta">Generated: ${new Date().toLocaleString()}</div>
        </div>

        <div class="card">
          <div class="section-title">Analysis</div>
          <div class="meta">Found sections: ${Object.keys(analysis?.found || {}).join(", ") || "—"}</div>
          <div class="meta">Missing: ${(analysis?.missing || []).join(", ") || "—"}</div>
          <div style="margin-top:8px;" class="meta">ATS Score: ${atsScore !== null ? atsScore + "%" : "N/A"}</div>
        </div>

        <div class="card">
          <div class="section-title">Resume Text</div>
          <pre>${escapeHtml(resumeText)}</pre>
        </div>

        <div class="card">
          <div class="section-title">Job Description (for ATS)</div>
          <pre>${escapeHtml(jobDescription)}</pre>
        </div>

        <script>
          // auto-print
          setTimeout(()=>{ window.print(); }, 300);
        </script>
      </body>
      </html>
    `;

    const w = window.open("", "_blank", "width=900,height=800");
    if (!w) {
      alert("Popup blocked. Allow popups for this site to download PDF.");
      return;
    }
    w.document.open();
    w.document.write(html);
    w.document.close();
  };

  // small helper to escape HTML in the printable content
  function escapeHtml(text) {
    if (!text) return "";
    return text
      .replaceAll("&", "&amp;")
      .replaceAll("<", "&lt;")
      .replaceAll(">", "&gt;");
  }

  const clearAll = () => {
    if (!confirm("Clear resume & job description? This cannot be undone.")) return;
    setResumeText("");
    setJobDescription("");
    setAnalysis(null);
    setAtsScore(null);
    localStorage.removeItem("resumeText");
    localStorage.removeItem("jobDescription");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-white text-slate-900 p-6">
      {/* Header */}
      <header className="max-w-6xl mx-auto flex items-center justify-between mb-8">
        <div className="flex items-center gap-4">
          <div className="w-12 h-12 rounded-md bg-gradient-to-r from-indigo-600 to-pink-500 flex items-center justify-center shadow-lg transform -rotate-6">
            <svg width="22" height="22" viewBox="0 0 24 24" fill="none" className="text-white">
              <path d="M12 3v18" stroke="white" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M3 12h18" stroke="white" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </div>
          <div>
            <h1 className="text-2xl font-extrabold">Dynamic Resume Analyzer</h1>
            <p className="text-sm text-slate-500">Fast parsing, ATS match, and printable resume preview — beautiful UI, zero external libs.</p>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <button
            onClick={downloadPdf}
            className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-600 text-white rounded-md shadow hover:shadow-lg transition"
            title="Download printable PDF"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M12 3v12" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/><path d="M7 10l5 5 5-5" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/></svg>
            Save as PDF
          </button>

          <button
            onClick={() => { analyzeResume(); alert("Re-analyzed."); }}
            className="px-4 py-2 border rounded-md text-sm bg-white hover:bg-slate-50 transition"
            title="Re-run analysis"
          >
            Re-analyze
          </button>

          <button
            onClick={clearAll}
            className="px-3 py-2 text-sm rounded-md border text-rose-600 hover:bg-rose-50 transition"
            title="Clear everything"
          >
            Clear
          </button>
        </div>
      </header>

      {/* Main */}
      <main className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-12 gap-6">
        {/* Left column — controls */}
        <aside className="lg:col-span-5 space-y-6">
          {/* Upload card */}
          <div
            className={`rounded-2xl p-5 shadow-md border ${dragActive ? "border-indigo-300 bg-indigo-50" : "border-slate-100 bg-white"}`}
            onDragEnter={() => setDragActive(true)}
            onDragLeave={() => setDragActive(false)}
            onDragOver={(e) => e.preventDefault()}
            onDrop={handleDrop}
          >
            <div className="flex items-start justify-between">
              <div>
                <h2 className="text-lg font-semibold">Upload / Paste Resume</h2>
                <p className="text-sm text-slate-500">Upload a plain .txt file or paste your resume text below for instant analysis.</p>
              </div>
              <div className="text-sm text-slate-400">Tip: drag & drop</div>
            </div>

            <div className="mt-4 grid grid-cols-1 gap-3">
              <input
                type="file"
                accept=".txt"
                onChange={(e) => handleFile(e.target.files[0])}
                className="text-sm"
                title="Upload .txt resume"
              />

              <textarea
                value={resumeText}
                onChange={(e) => setResumeText(e.target.value)}
                placeholder="Paste your resume text here..."
                className="w-full min-h-[180px] p-3 rounded-md border focus:ring-2 focus:ring-indigo-200 resize-y text-sm"
              />
            </div>
          </div>

          {/* Job description card */}
          <div className="rounded-2xl p-5 shadow-md bg-white border">
            <h3 className="text-lg font-semibold">Job Description (for ATS)</h3>
            <p className="text-sm text-slate-500">Paste the JD so the analyzer can compute keyword match with your resume.</p>
            <textarea
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
              placeholder="Paste the job description here..."
              className="w-full min-h-[140px] p-3 mt-3 rounded-md border focus:ring-2 focus:ring-indigo-200 text-sm resize-y"
            />
            <div className="flex items-center justify-between mt-3">
              <div className="text-sm text-slate-500">Autosaved locally</div>
              <div className="flex items-center gap-3">
                <button
                  onClick={() => { setJobDescription(""); localStorage.removeItem("jobDescription"); }}
                  className="text-sm px-3 py-1 border rounded text-slate-600 hover:bg-slate-50"
                >
                  Clear JD
                </button>
                <button
                  onClick={() => { analyzeResume(); }}
                  className="px-3 py-1 bg-indigo-600 text-white rounded text-sm shadow"
                >
                  Analyze Now
                </button>
              </div>
            </div>
          </div>

          {/* Analysis summary */}
          <div className="rounded-2xl p-5 shadow-md bg-gradient-to-tr from-white to-slate-50 border">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-lg font-semibold">Quick Analysis</h3>
              <div className="text-sm text-slate-500">{analysis ? "Updated" : "No data"}</div>
            </div>

            {/* ATS visual */}
            <div className="mb-4">
              <div className="text-xs font-medium text-slate-600 mb-1">ATS Match</div>
              <div className="w-full bg-slate-200 rounded-full h-3">
                <div
                  style={{ width: `${Math.max(0, atsScore || 0)}%` }}
                  className={`h-3 rounded-full transition-all duration-700 ${
                    atsScore === null ? "bg-slate-400" : atsScore < 50 ? "bg-rose-500" : atsScore < 80 ? "bg-yellow-500" : "bg-emerald-500"
                  }`}
                />
              </div>
              <div className="mt-2 text-sm font-semibold">
                {atsScore !== null ? `${atsScore}%` : "Paste JD to compute"}
              </div>
            </div>

            {/* Found/Missing */}
            <div className="grid grid-cols-2 gap-3">
              <div className="p-3 rounded-md bg-emerald-50 border">
                <div className="text-xs text-emerald-700 font-semibold">Found</div>
                <div className="text-sm mt-2 text-emerald-800">
                  {analysis ? Object.keys(analysis.found).length : 0}
                </div>
              </div>

              <div className="p-3 rounded-md bg-rose-50 border">
                <div className="text-xs text-rose-700 font-semibold">Missing</div>
                <div className="text-sm mt-2 text-rose-800">
                  {analysis ? analysis.missing.length : 0}
                </div>
              </div>
            </div>

            {/* Feedback list */}
            <div className="mt-4">
              <div className="text-xs font-medium text-slate-600 mb-2">Feedback</div>
              <ul className="list-disc ml-5 text-sm text-slate-700 space-y-1">
                {feedback.map((f, i) => (
                  <li key={i}>{f}</li>
                ))}
              </ul>
            </div>
          </div>
        </aside>

        {/* Right column — live preview & analysis details */}
        <section className="lg:col-span-7 space-y-6">
          <div className="rounded-2xl p-6 shadow-lg bg-white border" ref={previewRef}>
            <div className="flex items-start justify-between gap-4">
              <div>
                <h2 className="text-xl font-bold">Live Resume Preview</h2>
                <p className="text-sm text-slate-500">How your resume content looks and what the analyzer detected.</p>
              </div>
              <div className="text-sm text-slate-400">
                <div>{new Date().toLocaleString()}</div>
              </div>
            </div>

            <div className="mt-5 grid grid-cols-1 md:grid-cols-3 gap-5">
              {/* Profile card */}
              <div className="col-span-1 md:col-span-1 p-4 rounded-lg border bg-gradient-to-b from-white to-slate-50">
                <div className="h-20 w-20 rounded-md bg-indigo-100 flex items-center justify-center mb-3">
                  <svg width="34" height="34" viewBox="0 0 24 24" fill="none"><path d="M12 12a4 4 0 1 0 0-8 4 4 0 0 0 0 8z" stroke="#4f46e5" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round"/><path d="M20 21v-1a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v1" stroke="#7c3aed" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round"/></svg>
                </div>
                <div>
                  <div className="text-sm font-semibold">Candidate</div>
                  <div className="text-xs text-slate-500">Live preview of parsed content</div>
                </div>
                <div className="mt-3 text-xs text-slate-600">
                  <div className="font-semibold">Detected sections</div>
                  <div className="mt-1">
                    {analysis ? (
                      <div className="flex flex-wrap gap-2 mt-2">
                        {Object.keys(SECTION).map((s) => (
                          <span key={s} className={`px-2 py-1 text-xs rounded-full ${analysis.found[s] ? "bg-emerald-100 text-emerald-800" : "bg-rose-50 text-rose-800"}`}>
                            {s}
                          </span>
                        ))}
                      </div>
                    ) : (
                      <div className="text-xs text-slate-400">No analysis yet</div>
                    )}
                  </div>
                </div>
              </div>

              {/* Main preview panel */}
              <div className="col-span-2 md:col-span-2 p-4 rounded-lg border bg-white max-h-[420px] overflow-auto">
                {renderPreviewSections()}
              </div>
            </div>

            {/* Detailed analysis table */}
            <div className="mt-6">
              <h4 className="text-sm font-semibold mb-2">Detailed Analysis</h4>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="p-3 rounded-md border bg-slate-50">
                  <div className="text-xs text-slate-600">Found</div>
                  <div className="mt-2 text-sm text-slate-800">
                    {analysis ? (Object.keys(analysis.found).length > 0 ? Object.keys(analysis.found).join(", ") : "—") : "—"}
                  </div>
                </div>
                <div className="p-3 rounded-md border bg-slate-50">
                  <div className="text-xs text-slate-600">Missing</div>
                  <div className="mt-2 text-sm text-slate-800">
                    {analysis ? (analysis.missing.length > 0 ? analysis.missing.join(", ") : "None") : "—"}
                  </div>
                </div>
                <div className="p-3 rounded-md border bg-slate-50">
                  <div className="text-xs text-slate-600">Keyword Match</div>
                  <div className="mt-2 text-sm text-slate-800">
                    {atsScore !== null ? `${atsScore}%` : "Paste JD to compute"}
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Suggestions / next steps */}
          <div className="rounded-2xl p-5 shadow-md bg-white border">
            <h4 className="text-lg font-semibold mb-2">Suggestions</h4>
            <ol className="list-decimal ml-5 text-sm text-slate-700 space-y-2">
              <li>Make sure your Skills section contains both hard skills and role-specific keywords.</li>
              <li>Match exact job description phrases in experience bullets where true — ATS favors direct matches.</li>
              <li>Use this preview for a quick PDF export (Save as PDF) or refine content on the left and re-analyze.</li>
            </ol>
          </div>
        </section>
      </main>

      <footer className="max-w-6xl mx-auto mt-8 text-sm text-slate-500 text-center">
        Built with 💙 — Tailwind + React • No external libraries • Your data stays in the browser
      </footer>
    </div>
  );
}
