import React, { useState, useRef, useEffect } from "react";

const RESPONSES = {
  greeting: "Hello! Iâ€™m your Resume Assistant ðŸ‘‹",
  ats: "ATS scans for keywords, clean structure, and standard headings. Tailor your resume to the JD.",
  keywords: "Pick high-signal JD terms, use action verbs, and quantify results (%, $, time).",
  format: "Keep it simple: headings, bullets, consistent dates, no tables/graphics.",
  sections: "Must-have: Contact, Summary, Experience, Skills, Education. Add Projects/Certs if relevant.",
  default: "Ask me about ATS, keywords, formatting, or sections.",
};

export default function Chatbot({ onClose }) {
  const [messages, setMessages] = useState([{ type: "bot", text: RESPONSES.greeting }]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  // Scroll to bottom when messages update
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const replyFor = (text) => {
    const t = text.toLowerCase();
    if (t.includes("ats")) return RESPONSES.ats;
    if (t.includes("keyword")) return RESPONSES.keywords;
    if (t.includes("format")) return RESPONSES.format;
    if (t.includes("section")) return RESPONSES.sections;
    if (t.includes("hello") || t.includes("hi")) return RESPONSES.greeting;
    return RESPONSES.default;
  };

  const send = () => {
  const text = input.trim();
  if (!text) return;
  setMessages(prev => [...prev, { type: "user", text }]);
  setInput("");
  setTimeout(() => {
    setMessages(prev => [...prev, { type: "bot", text: replyFor(text) }]);
  }, 200);
};
  return (
    <div className="chatbot-overlay" onClick={onClose}>
      <div className="chatbot" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="chat-header">
          <h3>ðŸ¤– Resume Assistant</h3>
          <button className="x" onClick={onClose} aria-label="Close">âœ•</button>
        </div>

        {/* Messages */}
        <div className="chat-messages">
          {messages.map((m, i) => (
            <div key={i} className={`chat-message ${m.type}`}>
              {m.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input */}
        <div className="chat-input-row">
          <input
            autoFocus
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && send()}
            placeholder="Ask about ATS, keywords, formatâ€¦"
          />
          <button onClick={send}>Send</button>
        </div>
      </div>
    </div>
  );
}
