import React, { useState, useEffect, useMemo, useRef } from "react";

const SECTION = {
  education: /(education|qualifications|academic)/i,
  skills: /(skills|technical skills|key skills|competencies)/i,
  experience: /(experience|work experience|professional experience|employment)/i,
  projects: /(projects|personal projects|project experience)/i,
  certifications: /(certifications|certificates|licenses)/i,
  contact: /(contact|email|phone|linkedin|github|website)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState(
    () => localStorage.getItem("resumeText") || ""
  );
  const [jobDescription, setJobDescription] = useState(
    () => localStorage.getItem("jobDescription") || ""
  );
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [modal, setModal] = useState({ show: false, message: "", confirm: null });
  const previewRef = useRef();

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
    localStorage.setItem("resumeText", resumeText);
  }, [resumeText, jobDescription]);

  useEffect(() => {
    localStorage.setItem("jobDescription", jobDescription);
  }, [jobDescription]);

  const handleFile = (file) => {
    if (!file) return;
    if (file.type !== "text/plain") {
      return setModal({ show: true, message: "Only .txt resume uploads are supported.", confirm: null });
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) =>
    text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTION)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }
    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = Array.from(new Set(splitWords(jobDescription)));
      const resumeWords = splitWords(resumeText);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }
    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}`);
    }
    if (atsScore !== null) {
      if (atsScore < 45) fb.push("ATS Match: Low. Add role-specific keywords.");
      else if (atsScore < 75) fb.push("ATS Match: Moderate. Add more targeted keywords.");
      else fb.push("ATS Match: Strong. Good alignment.");
    }
    return fb;
  }, [analysis, atsScore]);

  const renderPreviewSections = () => {
    if (!resumeText.trim()) {
      return <div className="text-sm text-gray-500 italic">Paste your resume text to see a live preview here.</div>;
    }
    const parts = resumeText.split(/\n{2,}/).map((p) => p.trim()).filter(Boolean);
    return parts.slice(0, 8).map((p, i) => (
      <section key={i} className="mb-4">
        <h4 className="text-sm font-semibold text-gray-700 mb-1">{p.split("\n")[0].slice(0, 60)}</h4>
        <p className="text-sm text-gray-600 leading-relaxed whitespace-pre-wrap">{p}</p>
      </section>
    ));
  };

  const downloadPdf = () => {
    const content = previewRef.current;
    if (!content) {
      return setModal({ show: true, message: "Nothing to print. Paste resume text first.", confirm: null });
    }
    const title = "Resume — Dynamic Resume Analyzer";
    const html = `
      <html>
      <head>
        <title>${title}</title>
        <style>
          body { font-family: Arial, sans-serif; margin: 24px; color: #111827; }
          h1 { font-size: 20px; margin: 0 0 12px 0; }
          .card { border: 1px solid #e5e7eb; border-radius:8px; padding:14px; margin-bottom:12px; }
          .section-title { font-weight:700; margin-bottom:6px; }
          pre { white-space:pre-wrap; word-break:break-word; font-family:inherit; font-size:13px; }
        </style>
      </head>
      <body>
        <h1>Resume Preview</h1>
        <div class="card">
          <div class="section-title">Analysis</div>
          <div>Found: ${Object.keys(analysis?.found || {}).join(", ") || "—"}</div>
          <div>Missing: ${(analysis?.missing || []).join(", ") || "—"}</div>
          <div>ATS Score: ${atsScore !== null ? atsScore + "%" : "N/A"}</div>
        </div>
        <div class="card"><div class="section-title">Resume</div><pre>${escapeHtml(resumeText)}</pre></div>
        <div class="card"><div class="section-title">Job Description</div><pre>${escapeHtml(jobDescription)}</pre></div>
        <script>setTimeout(()=>{window.print();},300)</script>
      </body>
      </html>
    `;
    const w = window.open("", "_blank", "width=900,height=800");
    if (!w) {
      return setModal({ show: true, message: "Popup blocked. Allow popups for this site.", confirm: null });
    }
    w.document.open();
    w.document.write(html);
    w.document.close();
  };

  const escapeHtml = (t) =>
    t ? t.replaceAll("&", "&amp;").replaceAll("<", "&lt;").replaceAll(">", "&gt;") : "";

  const clearAll = () => {
    setModal({
      show: true,
      message: "Clear resume and job description? This cannot be undone.",
      confirm: () => {
        setResumeText("");
        setJobDescription("");
        setAnalysis(null);
        setAtsScore(null);
        localStorage.removeItem("resumeText");
        localStorage.removeItem("jobDescription");
      },
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-white to-gray-200 text-gray-900">
      <header className="max-w-6xl mx-auto flex items-center justify-between py-6 px-4">
        <div>
          <h1 className="text-2xl font-bold tracking-tight">Dynamic Resume Analyzer</h1>
          <p className="text-sm text-gray-500">Analyze, preview, and optimize your resume in real-time.</p>
        </div>
        <div className="flex gap-3">
          <button onClick={downloadPdf} className="px-4 py-2 bg-indigo-600 text-white rounded-md shadow hover:bg-indigo-700">Download PDF</button>
          <button onClick={clearAll} className="px-4 py-2 border rounded-md hover:bg-gray-50">Clear</button>
        </div>
      </header>

      <main className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-12 gap-6 px-4 pb-10">
        <aside className="lg:col-span-5 space-y-6">
          <div
            className={`rounded-xl p-5 shadow border ${dragActive ? "border-indigo-400 bg-indigo-50" : "bg-white"}`}
            onDragEnter={() => setDragActive(true)}
            onDragLeave={() => setDragActive(false)}
            onDragOver={(e) => e.preventDefault()}
            onDrop={handleDrop}
          >
            <h2 className="text-lg font-semibold mb-2">Upload or Paste Resume</h2>
            <input type="file" accept=".txt" onChange={(e) => handleFile(e.target.files[0])} className="mb-3 text-sm" />
            <textarea
              value={resumeText}
              onChange={(e) => setResumeText(e.target.value)}
              placeholder="Paste your resume text here..."
              className="w-full min-h-[180px] p-3 border rounded-md text-sm resize-y focus:ring-2 focus:ring-indigo-400"
            />
          </div>
          <div className="rounded-xl p-5 shadow bg-white border">
            <h3 className="text-lg font-semibold mb-2">Job Description</h3>
            <textarea
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
              placeholder="Paste the job description..."
              className="w-full min-h-[140px] p-3 border rounded-md text-sm resize-y focus:ring-2 focus:ring-indigo-400"
            />
            <div className="flex justify-end mt-3">
              <button onClick={analyzeResume} className="px-3 py-2 bg-indigo-600 text-white rounded-md shadow hover:bg-indigo-700">Analyze</button>
            </div>
          </div>
          <div className="rounded-xl p-5 shadow bg-white border">
            <h3 className="text-lg font-semibold mb-4">Analysis Summary</h3>
            <div>
              <div className="text-sm font-medium mb-1">ATS Score</div>
              <div className="w-full bg-gray-200 rounded-full h-3">
                <div
                  style={{ width: `${atsScore || 0}%` }}
                  className={`h-3 rounded-full transition-all duration-700 ${atsScore === null ? "bg-gray-400" : atsScore < 50 ? "bg-red-500" : atsScore < 80 ? "bg-yellow-500" : "bg-green-500"}`}
                />
              </div>
              <div className="mt-2 text-sm">{atsScore !== null ? `${atsScore}%` : "Paste JD to compute"}</div>
            </div>
            <div className="mt-4">
              <div className="text-sm font-medium mb-1">Feedback</div>
              <ul className="list-disc ml-5 text-sm text-gray-700 space-y-1">
                {feedback.map((f, i) => <li key={i}>{f}</li>)}
              </ul>
            </div>
          </div>
        </aside>
        <section className="lg:col-span-7 space-y-6">
          <div ref={previewRef} className="rounded-xl p-6 shadow bg-white border max-h-[600px] overflow-auto">
            <h2 className="text-xl font-semibold mb-4">Live Resume Preview</h2>
            {renderPreviewSections()}
          </div>
        </section>
      </main>

      {modal.show && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
            <p className="text-gray-800">{modal.message}</p>
            <div className="flex justify-end gap-3 mt-6">
              {modal.confirm && (
                <button
                  onClick={() => {
                    modal.confirm();
                    setModal({ show: false, message: "", confirm: null });
                  }}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-md"
                >
                  Yes
                </button>
              )}
              <button
                onClick={() => setModal({ show: false, message: "", confirm: null })}
                className="px-4 py-2 border rounded-md hover:bg-gray-50"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
