import React, { useState } from "react";

// !!! CRITICAL: PASTE YOUR DEPLOYED GOOGLE APPS SCRIPT WEB APP URL HERE !!!
// Using the latest URL provided:
const APPS_SCRIPT_URL = "https://script.google.com/macros/s/AKfycbwt3y2Qq_nWTqwrlZykkamyOe3QsQERFmaOK2wwhqg504QSQ2bx7lv_G7bjAMeWzvbg/exec";

/**
 * Authentication Component for Login/Register using Google Apps Script as backend.
 */
export default function Auth({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState(null);

  const action = isLogin ? "login" : "register";
  const formTitle = isLogin ? "Login" : "Register";

  // Utility for robust API calls with exponential backoff
  const fetchWithRetry = async (url, options, retries = 3) => {
    for (let i = 0; i < retries; i++) {
      try {
        const response = await fetch(url, options);
        if (!response.ok) {
            // If the script returned an error object, try to read it, otherwise throw generic error
            const errorText = await response.text();
            throw new Error(`HTTP error! Status: ${response.status}. Response: ${errorText.substring(0, 50)}...`);
        }
        return response;
      } catch (error) {
        if (i < retries - 1) {
          const delay = Math.pow(2, i) * 1000;
          await new Promise(resolve => setTimeout(resolve, delay));
          console.warn(`Retry ${i + 1} for ${action} failed. Retrying in ${delay / 1000}s...`);
        } else {
          throw error;
        }
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
    setIsLoading(true);

    try {
      const response = await fetchWithRetry(APPS_SCRIPT_URL, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            action: action,
            email: email,
            password: password
        })
      });

      const result = await response.json();

      if (result.success) {
        setMessage({ type: 'success', text: result.message });
        setTimeout(() => onLogin(email), 1500); 
      } else {
        setMessage({ type: 'error', text: result.message || `An unknown server error occurred during ${action}.` });
      }

    } catch (error) {
      console.error(`Fetch Error:`, error);
      // Display the specific error message to the user
      setMessage({ type: 'error', text: `Connection Error: ${error.message}` });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="card auth-card">
        <h2 className="section-title center">{formTitle} to use the App</h2>
        
        {/* API Message Display */}
        {message && (
          <div className={`api-message ${message.type === 'error' ? 'error' : 'success'}`}>
            {message.text}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <input
            className="text-input"
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={isLoading}
          />
          <input
            className="text-input"
            type="password"
            placeholder="Password (Must be 6+ chars)"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            minLength="6"
            required
            disabled={isLoading}
          />
          <button type="submit" className="btn primary full-width" disabled={isLoading}>
            {isLoading ? "Processing..." : formTitle}
          </button>
        </form>
        
        <div className="switch-auth center">
          {isLogin ? "Need an account?" : "Already have an account?"}
          <button 
            className="link-button" 
            onClick={() => {
                setIsLogin(!isLogin);
                setMessage(null);
            }} 
            type="button"
            disabled={isLoading}
          >
            {isLogin ? "Register" : "Login"}
          </button>
        </div>
        <p className="hint center mt-1">
          <small>
            Authentication uses Google Apps Script as the backend API.
          </small>
        </p>
      </div>
    </div>
  );
}
