import React, { useState, useEffect, useMemo } from "react";

const SECTION_PATTERNS = {
  education: /(education|qualifications)/i,
  skills: /(skills|technical skills|key skills)/i,
  experience: /(experience|work experience )/i,
  projects: /(projects)/i,
  certifications: /(certifications)/i,
  contact: /(contact|email|phone|linkedin|github)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
  }, [resumeText, jobDescription]);

  const handleFile = (file) => {
    if (!file || file.type !== "text/plain") {
      return alert("OI");
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) => text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];

    for (const [key, regex] of Object.entries(SECTION_PATTERNS)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }

    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = splitWords(jobDescription);
      const resumeWords = splitWords(resumeText);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }

    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}.`);
    }
    if (atsScore !== null) {
      if (atsScore < 50) fb.push("ATS score is low. Add more keywords from the job description.");
      else if (atsScore < 80) fb.push("Decent match. Add more relevant skills to boost your score.");
      else fb.push("Strong alignment with JD. Focus on formatting and readability.");
    }
    return fb;
  }, [analysis, atsScore]);

  return (
    <div
      className="min-h-screen bg-gray-100 text-gray-900 flex flex-col items-center p-6"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      <h1 className="text-4xl font-bold mb-6">Dynamic Resume Analyzer</h1>

      {/* Upload Section */}
      <div className="w-full max-w-4xl mb-6">
        <label className="block mb-2 font-semibold">Upload Resume (.txt)</label>
        <input
          type="file"
          accept=".txt"
          onChange={(e) => handleFile(e.target.files[0])}
          className="mb-4"
        />
        <div className="border-2 border-dashed rounded-lg p-6 text-center text-gray-500">
          Drag & Drop resume here
        </div>
      </div>

      {/* Job Description */}
      <div className="w-full max-w-4xl mb-6">
        <h2 className="text-xl font-semibold mb-2">Job Description</h2>
        <textarea
          className="w-full min-h-[350px] max-h-[500px] p-4 border rounded-lg shadow-sm resize-y overflow-y-auto"
          placeholder="Paste the job description here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />
      </div>

      {/* Results */}
      {analysis && (
        <div className="w-full max-w-4xl bg-white p-6 rounded-xl shadow-md space-y-6">
          <h2 className="text-2xl font-bold">Analysis Results</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <h3 className="font-semibold mb-2">Found Sections</h3>
              <ul className="list-disc ml-6 text-green-700">
                {Object.keys(analysis.found).map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="font-semibold mb-2">Missing Sections</h3>
              <ul className="list-disc ml-6 text-red-600">
                {analysis.missing.map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="font-semibold mb-2">ATS Score</h3>
              <p className="text-lg font-bold">
                {atsScore !== null ? `${atsScore}% match` : "Paste JD to calculate score"}
              </p>
            </div>
          </div>

          {/* Feedback */}
          <div>
            <h3 className="font-semibold mb-2">Feedback</h3>
            <ul className="list-disc ml-6 text-blue-700">
              {feedback.map((f, i) => (
                <li key={i}>{f}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
