import React, { useState } from "react";
// Import Auth and the App.css file
import Auth from "./Auth";
import "./App.css";

// --- DUMMY COMPONENTS (For runnable demo) ---

const ResumeAnalyzer = () => (
  <div className="card" style={{padding:'40px', textAlign:'center', border:'1px solid #ccc', borderRadius:'16px', fontWeight: 600}}>
    <h3>Authentication Successful!</h3>
    <p>Application Content Loaded Here.</p>
    <br/>
    <p>This section is secured. Your user: **{/* userEmail will be displayed in the header */}**</p>
  </div>
);

const Chatbot = ({ onClose }) => (
    <div className="chatbot-overlay" onClick={onClose}>
        <div className="chatbot" onClick={(e) => e.stopPropagation()}>
            <div className="chat-header">Chatbot Assistant <button className="x" onClick={onClose}>âœ•</button></div>
            <div className="chat-messages">Chat content...</div>
            <div className="chat-input-row"><input placeholder="Type a message..." /><button>Send</button></div>
        </div>
    </div>
);

// ---------------------------------------------


export default function App() {
  const [showChatbot, setShowChatbot] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userEmail, setUserEmail] = useState(null);

  const handleLogin = (email) => {
    setIsLoggedIn(true);
    setUserEmail(email);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserEmail(null);
    console.log("Logged out successfully.");
  };

  return (
    <div className="app">
      {/* IMPROVED HEADER (with login/logout) */}
      <header className="hero">
        <div className="hero-content">
          <div className="header-row">
            <h1 className="title-small">ðŸš€ Google Sheet Auth Demo</h1>
            {isLoggedIn && (
              <div className="auth-info">
                <span className="user-email">{userEmail}</span>
                <button className="btn logout-btn" onClick={handleLogout}>
                  Logout
                </button>
              </div>
            )}
          </div>
          <p className="subtitle">Real-time authentication powered by Google Apps Script</p>
        </div>
      </header>

      {/* Main Content conditionally rendered based on authentication */}
      <main className="container">
        {isLoggedIn ? (
          <ResumeAnalyzer />
        ) : (
          <Auth onLogin={handleLogin} />
        )}
      </main>

      {/* FAB (Floating Action Button) for Chatbot */}
      {isLoggedIn && (
        <>
          <button
            className={`fab ${showChatbot ? 'fab-active' : ''}`}
            aria-label="Open App Assistant"
            onClick={() => setShowChatbot(!showChatbot)}
          >
            {showChatbot ? 'âœ•' : 'ðŸ’¬'}
          </button>
          {showChatbot && <Chatbot onClose={() => setShowChatbot(false)} />}
        </>
      )}

      {/* FOOTER */}
      <footer className="footer-v2">
        <div className="footer-content">
          <p className="footer-title">Google Sheet Authentication Demo</p>
          <div className="team-grid">
            <div className="team-member">
              <strong>Frontend</strong> - V C Ramjhith
            </div>
            <div className="team-member">
              <strong>Backend</strong> - Vamshi
            </div>
            <div className="team-member">
              <strong>QA Tester</strong> - Varshini
            </div>
            <div className="team-member">
              <strong>Team Leader</strong> - U Shivakumar
            </div>
          </div>
          <p className="footer-copyright">
            Â© 2025 Google Sheet Auth Demo | Powered by React and GAS.
          </p>
        </div>
      </footer>
    </div>
  );
}
