import React, { useEffect, useMemo, useRef, useState } from "react";
// Optional frontend-only parsers for PDFs & DOCX
// Install: npm i pdfjs-dist mammoth
import { getDocument } from "pdfjs-dist";
import * as mammoth from "mammoth/mammoth.browser";

// ====== Utility: Debounce ======
function debounce(fn, wait = 400) {
  let t;
  return (...args) => {
    clearTimeout(t);
    t = setTimeout(() => fn(...args), wait);
  };
}

// ====== Theme Palettes (Tailwind classes) ======
const THEMES = {
  light: {
    bg: "bg-gray-50",
    card: "bg-white",
    text: "text-gray-900",
    subtle: "text-gray-600",
    border: "border-gray-200",
    accent: "bg-blue-600 hover:bg-blue-700 text-white",
    pill: "bg-blue-100 text-blue-800",
    danger: "bg-red-600 hover:bg-red-700 text-white",
    ok: "text-emerald-700",
    bad: "text-rose-700",
  },
  dark: {
    bg: "bg-gray-950",
    card: "bg-gray-900",
    text: "text-gray-100",
    subtle: "text-gray-400",
    border: "border-gray-800",
    accent: "bg-indigo-600 hover:bg-indigo-700 text-white",
    pill: "bg-indigo-900 text-indigo-200",
    danger: "bg-rose-600 hover:bg-rose-700 text-white",
    ok: "text-emerald-300",
    bad: "text-rose-300",
  },
  ocean: {
    bg: "bg-sky-50",
    card: "bg-white",
    text: "text-slate-900",
    subtle: "text-slate-600",
    border: "border-sky-200",
    accent: "bg-sky-600 hover:bg-sky-700 text-white",
    pill: "bg-sky-100 text-sky-800",
    danger: "bg-rose-600 hover:bg-rose-700 text-white",
    ok: "text-emerald-700",
    bad: "text-rose-700",
  },
};

// ====== Regex patterns for common resume sections ======
const SECTION_PATTERNS = {
  contact: /(contact|email|e-mail|phone|linkedin|github|portfolio|address)/i,
  summary: /(summary|objective|about me)/i,
  skills: /(skills|technical skills|key skills|competencies|tech stack)/i,
  experience: /(experience|work history|employment|professional experience)/i,
  projects: /(projects|personal projects|portfolio)/i,
  education: /(education|academics|qualifications|coursework)/i,
  certifications: /(certifications|licenses|certificates)/i,
  awards: /(awards|achievements|honors)/i,
};

const COMMON_KEYWORDS = [
  "javascript","react","node","python","java","c++","sql","aws","docker","kubernetes","rest","graphql","typescript","html","css","tailwind","redux","firebase","git","linux","agile","jest","cypress","machine learning","nlp","data structures","algorithms"
];

export default function App() {
  const [theme, setTheme] = useState(() => localStorage.getItem("dra_theme") || "light");
  const T = THEMES[theme];

  const [resumeText, setResumeText] = useState("");
  const [jobDesc, setJobDesc] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(0);
  const [loading, setLoading] = useState(false);
  const [filename, setFilename] = useState("");
  const fileInputRef = useRef(null);

  useEffect(() => {
    localStorage.setItem("dra_theme", theme);
  }, [theme]);

  // ====== Parsing: PDF, DOCX, TXT ======
  async function extractTextFromPDF(file) {
    const arrayBuffer = await file.arrayBuffer();
    const loadingTask = getDocument({ data: arrayBuffer });
    const pdf = await loadingTask.promise;
    let fullText = "";
    for (let i = 1; i <= pdf.numPages; i++) {
      const page = await pdf.getPage(i);
      const content = await page.getTextContent();
      const strings = content.items.map((it) => it.str);
      fullText += strings.join(" ") + "\n";
    }
    return fullText;
  }

  async function extractTextFromDOCX(file) {
    const arrayBuffer = await file.arrayBuffer();
    const { value } = await mammoth.extractRawText({ arrayBuffer });
    return value || "";
  }

  async function handleFile(file) {
    if (!file) return;
    setLoading(true);
    setFilename(file.name);
    try {
      const ext = file.name.split(".").pop().toLowerCase();
      let text = "";
      if (ext === "pdf") text = await extractTextFromPDF(file);
      else if (ext === "docx") text = await extractTextFromDOCX(file);
      else if (ext === "txt") text = await file.text();
      else throw new Error("Unsupported file. Use PDF, DOCX, or TXT.");
      setResumeText(text);
    } catch (e) {
      alert(e.message || "Failed to parse file");
    } finally {
      setLoading(false);
    }
  }

  // ====== Core Analysis ======
  const doAnalyze = useMemo(
    () =>
      debounce((rText, jdText) => {
        const foundSections = {};
        const missingSections = [];
        Object.entries(SECTION_PATTERNS).forEach(([key, regex]) => {
          if (regex.test(rText)) foundSections[key] = true;
          else missingSections.push(key);
        });

        // Keyword overlap scoring
        const resumeLower = rText.toLowerCase();
        const jdLower = jdText.toLowerCase();

        const jdTokens = new Set(
          jdLower
            .replace(/[^a-z0-9+.# ]/g, " ")
            .split(/\s+/)
            .filter(Boolean)
        );

        const resumeTokens = new Set(
          resumeLower
            .replace(/[^a-z0-9+.# ]/g, " ")
            .split(/\s+/)
            .filter(Boolean)
        );

        let hits = 0;
        let targets = 0;
        COMMON_KEYWORDS.forEach((kw) => {
          if (jdTokens.has(kw)) {
            targets++;
            if (resumeTokens.has(kw)) hits++;
          }
        });

        // Section weight + keyword weight
        const sectionWeight = Object.keys(SECTION_PATTERNS).length;
        const sectionScore = Object.keys(foundSections).length / sectionWeight;
        const keywordScore = targets === 0 ? 0.5 : hits / targets; // if JD empty of known kws, neutral 0.5

        // Contact sanity checks
        const contactSignals = [
          /[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i,
          /\+?\d[\d\s()-]{7,}/,
          /linkedin\.com\//i,
          /github\.com\//i,
        ];
        const contactScore = contactSignals.reduce((acc, rx) => acc + (rx.test(resumeLower) ? 1 : 0), 0) / contactSignals.length;

        // Length/format sanity: 300-4000 words reasonable
        const words = resumeLower.split(/\s+/).filter(Boolean);
        const lengthScore = Math.max(0, Math.min(1, (words.length - 300) / (2000 - 300)));

        const finalATS = Math.round((sectionScore * 0.35 + keywordScore * 0.4 + contactScore * 0.15 + lengthScore * 0.1) * 100);

        // Feedback suggestions
        const suggestions = [];
        if (missingSections.includes("summary")) suggestions.push("Add a concise 2–3 line Professional Summary targeting the JD.");
        if (missingSections.includes("skills")) suggestions.push("Include a Skills section with 8–15 tools/tech ordered by relevance.");
        if (missingSections.includes("experience")) suggestions.push("Detail Experience with action verbs, impact, and metrics (%, $, time saved).");
        if (missingSections.includes("projects")) suggestions.push("Add 2–3 projects showing tech used and measurable outcomes.");
        if (missingSections.includes("education")) suggestions.push("List degree, college, year, and key coursework if relevant.");
        if (missingSections.includes("certifications")) suggestions.push("Add notable certifications matching JD keywords.");
        if (!/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i.test(resumeLower)) suggestions.push("Include a professional email address.");
        if (!/linkedin\.com\//i.test(resumeLower)) suggestions.push("Link your LinkedIn profile.");
        if (targets > 0 && hits < Math.ceil(targets * 0.6)) suggestions.push("Mirror more JD keywords naturally in bullets (don’t stuff).");
        if (lengthScore < 0.2) suggestions.push("Resume may be too short; add context and results.");
        if (lengthScore > 0.9) suggestions.push("Resume may be too long; tighten to 1–2 pages with crisp bullets.");

        setAnalysis({ foundSections, missingSections, suggestions });
        setAtsScore(finalATS);
      }, 350),
    []
  );

  useEffect(() => {
    doAnalyze(resumeText, jobDesc);
  }, [resumeText, jobDesc, doAnalyze]);

  // ====== UI Helpers ======
  function Stat({ label, value }) {
    return (
      <div className={`p-4 rounded-2xl border ${T.border} ${T.card} flex flex-col gap-1 shadow-sm`}>
        <div className={`text-sm ${T.subtle}`}>{label}</div>
        <div className={`text-2xl font-semibold ${T.text}`}>{value}</div>
      </div>
    );
  }

  function Pill({ children }) {
    return <span className={`px-3 py-1 rounded-full text-xs font-medium ${T.pill}`}>{children}</span>;
  }

  return (
    <div className={`min-h-screen ${T.bg} ${T.text} p-6`}> 
      <div className="max-w-7xl mx-auto flex flex-col gap-6">
        {/* Header */}
        <header className="flex flex-col md:flex-row md:items-center md:justify-between gap-3">
          <div>
            <h1 className="text-3xl font-bold">Dynamic Resume Analyzer</h1>
            <p className={`${T.subtle}`}>Real-time parsing, ATS scoring, and actionable feedback — frontend‑only.</p>
          </div>
          <div className="flex items-center gap-2">
            <label className="text-sm">Theme</label>
            <select
              value={theme}
              onChange={(e) => setTheme(e.target.value)}
              className={`px-3 py-2 rounded-xl border ${T.border} ${T.card}`}
            >
              {Object.keys(THEMES).map((k) => (
                <option key={k} value={k}>{k}</option>
              ))}
            </select>
            <button
              onClick={() => fileInputRef.current?.click()}
              className={`px-4 py-2 rounded-xl ${T.accent}`}
              disabled={loading}
            >
              {loading ? "Parsing…" : "Upload Resume (PDF/DOCX/TXT)"}
            </button>
            <input
              ref={fileInputRef}
              type="file"
              accept=".pdf,.docx,.txt"
              className="hidden"
              onChange={(e) => handleFile(e.target.files?.[0])}
            />
          </div>
        </header>

        {/* Top Stats */}
        <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          <Stat label="ATS Score" value={`${atsScore}/100`} />
          <Stat label="Sections Found" value={analysis ? Object.keys(analysis.foundSections || {}).length : 0} />
          <Stat label="Missing Sections" value={analysis ? (analysis.missingSections || []).length : 0} />
          <Stat label="File" value={filename || "None"} />
        </section>

        {/* Editors */}
        <section className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className={`p-4 rounded-2xl border ${T.border} ${T.card} shadow-sm`}>
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-lg font-semibold">Resume Text</h2>
              <div className="flex gap-2">
                <button
                  className={`px-3 py-2 rounded-xl ${T.accent}`}
                  onClick={() => navigator.clipboard.writeText(resumeText)}
                >Copy</button>
                <button
                  className={`px-3 py-2 rounded-xl ${T.danger}`}
                  onClick={() => setResumeText("")}
                >Clear</button>
              </div>
            </div>
            <textarea
              className={`w-full h-[420px] md:h-[520px] resize-vertical p-4 rounded-xl border ${T.border} ${T.card} ${T.text}`}
              placeholder="Paste your resume text here or upload a file above…"
              value={resumeText}
              onChange={(e) => setResumeText(e.target.value)}
            />
            <div className={`mt-2 text-xs ${T.subtle}`}>Tip: Keep bullet points action‑driven and quantify impact.</div>
          </div>

          <div className={`p-4 rounded-2xl border ${T.border} ${T.card} shadow-sm`}>
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-lg font-semibold">Job Description</h2>
              <div className="flex gap-2">
                <button
                  className={`px-3 py-2 rounded-xl ${T.accent}`}
                  onClick={() => navigator.clipboard.writeText(jobDesc)}
                >Copy</button>
                <button
                  className={`px-3 py-2 rounded-xl ${T.danger}`}
                  onClick={() => setJobDesc("")}
                >Clear</button>
              </div>
            </div>
            <textarea
              className={`w-full h-[280px] md:h-[320px] resize-vertical p-4 rounded-xl border ${T.border} ${T.card} ${T.text}`}
              placeholder="Paste a job description here to tailor the analysis/ATS score…"
              value={jobDesc}
              onChange={(e) => setJobDesc(e.target.value)}
            />
            <div className="mt-3 flex flex-wrap gap-2">
              <Pill>Real‑time scoring</Pill>
              <Pill>Keyword matching</Pill>
              <Pill>Section health</Pill>
            </div>
          </div>
        </section>

        {/* Analysis */}
        <section className={`p-4 rounded-2xl border ${T.border} ${T.card} shadow-sm`}>
          <h2 className="text-lg font-semibold mb-4">Analysis & Feedback</h2>

          {!analysis ? (
            <div className={`${T.subtle}`}>Start by pasting your resume or uploading a file.</div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div className="lg:col-span-1 space-y-3">
                <div className="text-sm font-medium">Detected Sections</div>
                <ul className="space-y-2">
                  {Object.keys(SECTION_PATTERNS).map((key) => (
                    <li key={key} className="flex items-center justify-between">
                      <span className="capitalize">{key}</span>
                      {analysis.foundSections?.[key] ? (
                        <span className={`text-sm font-semibold ${T.ok}`}>Present ✓</span>
                      ) : (
                        <span className={`text-sm font-semibold ${T.bad}`}>Missing ✗</span>
                      )}
                    </li>
                  ))}
                </ul>
              </div>

              <div className="lg:col-span-2 space-y-3">
                <div className="text-sm font-medium">Actionable Feedback</div>
                {analysis.suggestions?.length ? (
                  <ul className="list-disc ml-5 space-y-2">
                    {analysis.suggestions.map((s, i) => (
                      <li key={i} className="text-sm">{s}</li>
                    ))}
                  </ul>
                ) : (
                  <div className={`${T.subtle}`}>Looks solid. Minor polish and you’re good.</div>
                )}

                <div className="mt-4">
                  <div className="text-sm font-medium mb-2">Quick Improvements</div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      className={`px-3 py-2 rounded-xl border ${T.border}`}
                      onClick={() => setResumeText((t) => t.replace(/responsible for/gi, "Led"))}
                    >Replace “responsible for” → “Led”</button>
                    <button
                      className={`px-3 py-2 rounded-xl border ${T.border}`}
                      onClick={() => setResumeText((t) => t.replace(/\bworked on\b/gi, "Developed"))}
                    >“Worked on” → “Developed”</button>
                    <button
                      className={`px-3 py-2 rounded-xl border ${T.border}`}
                      onClick={() => setResumeText((t) => t.replace(/\bhelped\b/gi, "Improved"))}
                    >“Helped” → “Improved”</button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </section>

        <footer className={`text-xs ${T.subtle} text-center py-4`}>
          Frontend‑only demo • Install deps: <code>npm i pdfjs-dist mammoth</code> • Add Tailwind
        </footer>
      </div>
    </div>
  );
}
