import React, { useEffect, useMemo, useRef, useState } from "react";

const SECTIONS = {
  contact: /\b(contact|email|phone|linkedin|github|address)\b/i,
  summary: /\b(summary|objective|profile)\b/i,
  experience: /\b(experience|work experience|employment|career)\b/i,
  skills: /\b(skills|technical skills|competencies|technologies)\b/i,
  education: /\b(education|degree|university|college)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?|license|licensed?)\b/i,
};

const TEMPLATES = {
  modern: {
    name: "Modern Professional",
    className: "theme-modern",
    fontStack: "ui-sans-serif, system-ui, Segoe UI, Arial",
  },
  minimal: {
    name: "Minimal Clean",
    className: "theme-minimal",
    fontStack: "Arial, Helvetica, sans-serif",
  },
  creative: {
    name: "Creative Bold",
    className: "theme-creative",
    fontStack: "Helvetica, Arial, sans-serif",
  },
};

const STOPWORDS = new Set([
  "and", "the", "with", "from", "that", "this", "your", "their", "our",
  "for", "into", "able", "will", "shall", "must", "have", "has", "had",
  "are", "was", "were", "you", "they", "them", "over", "under", "about",
  "above", "below", "not", "only", "but", "also", "more", "than", "such",
  "etc", "using", "use", "used", "strong", "good", "great", "work", "role",
  "team", "skills", "requirements", "responsibilities", "job", "description",
  "looking", "plus", "preferred", "required", "experience", "years", "year",
  "developer", "engineer", "data"
]);

function extractKeywords(text) {
  return [...new Set(
    text
      .toLowerCase()
      .split(/[^a-z0-9+#.]/i)
      .map((w) => w.trim())
      .filter((w) => w.length > 2 && !STOPWORDS.has(w))
  )];
}

function firstNonEmptyLine(s) {
  return (s || "")
    .split(/\r?\n/)
    .map((x) => x.trim())
    .find((x) => x.length > 0) || "Candidate Name";
}

function escapeHTML(s) {
  return s
    .replaceAll("&", "&amp;")
    .replaceAll("<", "&lt;")
    .replaceAll(">", "&gt;");
}

function plainToHTML(resumeText) {
  const lines = (resumeText || "").split(/\r?\n/);
  return lines
    .map((line) => {
      const trimmed = line.trim();
      if (!trimmed) return "";
      const isHeading = Object.values(SECTIONS).some((r) => r.test(trimmed));
      const isBullet = trimmed.match(/^[-*‚Ä¢\d.].*$/);
      if (isHeading) return `<h2>${escapeHTML(trimmed)}</h2>`;
      if (isBullet)
        return `<ul><li>${escapeHTML(trimmed.replace(/^[-*‚Ä¢\d.]\s*/, ""))}</li></ul>`;
      return `<p>${escapeHTML(trimmed)}</p>`;
    })
    .join("\n");
}

function calculateATSScore(resumeText, jobDescription) {
  const found = {};
  const missing = [];

  Object.entries(SECTIONS).forEach(([k, rgx]) => {
    if (rgx.test(resumeText)) found[k] = true;
    else missing.push(k);
  });

  const jdKeys = extractKeywords(jobDescription);
  const resumeLower = resumeText.toLowerCase();
  const matched = jdKeys.filter((k) => resumeLower.includes(k));
  const keywordMatchRatio = jdKeys.length ? matched.length / jdKeys.length : 1;
  const keywordScore = Math.round(keywordMatchRatio * 40);

  const coreSections = ["contact", "summary", "experience", "skills", "education"];
  const coreMissingCount = coreSections.filter((k) => missing.includes(k)).length;
  const structureScore = Math.max(0, 60 - coreMissingCount * 12);

  const atsScore = Math.max(0, Math.min(100, structureScore + keywordScore));
  const missingKeys = jdKeys.filter((k) => !resumeLower.includes(k));

  return { found, missing, matched, missingKeys, atsScore, uniqueJD: jdKeys.length };
}

export default function App({ userEmail, passwordHash }) {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const [analysis, setAnalysis] = useState(null);
  const [fileError, setFileError] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const previewRef = useRef(null);
  const fileInputRef = useRef(null);

  const APPS_SCRIPT_URL =
    "https://script.google.com/macros/s/AKfycbxTliIAVFRcTNT_5GybN289uKzg49sdKS6uM3Ryxlx1zYKWekqLHAHmMK49DdSxUSj_/exec";

  const handleFile = (file) => {
    setFileError(null);
    if (!file) return;
    if (file.size > 2 * 1024 * 1024) {
      setFileError("File too large (Max 2MB).");
      setResumeText("");
      return;
    }
    if (file.type === "application/pdf") {
      setFileError(
        "‚ö†Ô∏è PDF selected. For accurate analysis, use a .txt file. Dummy content used."
      );
      setResumeText(
        "Candidate Name\n\nContact Information\n\nSummary\n\nExperience\n\nSkills: React, JavaScript, Python\n\nEducation"
      );
      return;
    }
    if (file.type !== "text/plain") {
      setFileError("Upload a .txt or .pdf file only.");
      setResumeText("");
      return;
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(String(e.target.result || ""));
    reader.readAsText(file);
  };

  useEffect(() => {
    if (!resumeText) {
      setAnalysis(null);
      return;
    }
    setAnalysis(calculateATSScore(resumeText, jobDescription));
  }, [resumeText, jobDescription]);

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const list = [];
    if (analysis.missing.length > 0)
      list.push(`‚ùå Missing core sections: ${analysis.missing.join(", ")}.`);
    else list.push("‚úÖ All standard sections found.");

    const matchPct = analysis.uniqueJD
      ? Math.round((analysis.matched.length / analysis.uniqueJD) * 100)
      : 100;

    if (analysis.uniqueJD === 0)
      list.push("üîé No JD provided.");
    else if (matchPct < 40)
      list.push(`üî¥ Low Keyword Match (${matchPct}%)`);
    else if (matchPct < 70)
      list.push(`üü° Moderate Match (${matchPct}%)`);
    else list.push(`üü¢ Strong Match (${matchPct}%)`);

    list.push(`üìä Final ATS Score: ${analysis.atsScore}%`);
    return list;
  }, [analysis]);

  const candidateName = useMemo(() => firstNonEmptyLine(resumeText), [resumeText]);
  const htmlBody = useMemo(() => plainToHTML(resumeText), [resumeText]);

  const blobToBase64 = (blob) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result.split(",")[1]);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });

  const downloadPDF = async () => {
    if (!previewRef.current) return;
    if (!window.jsPDF || !window.html2canvas) {
      alert("Libraries still loading. Try again in a moment.");
      return;
    }

    if (!userEmail || !passwordHash) {
      alert("You must be logged in to upload your resume.");
      return;
    }

    setIsUploading(true);
    const node = previewRef.current;

    try {
      const canvas = await window.html2canvas(node, { scale: 3, backgroundColor: "#fff" });
      const img = canvas.toDataURL("image/png");
      const pdf = new window.jsPDF("p", "pt", "a4");

      const pageW = pdf.internal.pageSize.getWidth();
      const pageH = pdf.internal.pageSize.getHeight();
      const imgH = (canvas.height * pageW) / canvas.width;
      let y = 0, remaining = imgH;

      while (remaining > 0) {
        pdf.addImage(img, "PNG", 0, -y, pageW, imgH);
        remaining -= pageH;
        y += pageH;
        if (remaining > 0) pdf.addPage();
      }

      const fileName = `${candidateName.replace(/\s/g, "_")}_Resume.pdf`;
      pdf.save(fileName);

      const pdfBlob = pdf.output("blob");
      const base64 = await blobToBase64(pdfBlob);

      const payload = new URLSearchParams({
        action: "store",
        email: userEmail,
        passwordHash,
        fileName,
        resumeFile: base64,
      });

      const resp = await fetch(APPS_SCRIPT_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
        },
        body: payload.toString(),
      });

      const contentType = resp.headers.get("content-type") || "";
      if (!contentType.includes("application/json")) {
        const text = await resp.text();
        throw new Error("Non-JSON response: " + text);
      }

      const result = await resp.json();
      if (result.status === "success") {
        alert(`‚úÖ Resume uploaded! Link: ${result.link}`);
      } else {
        alert(`‚ùå Upload failed: ${result.message}`);
      }
    } catch (err) {
      alert(`‚ö†Ô∏è Error: ${err.message}`);
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <React.Fragment>
      <script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js"></script>
      <script src="https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js"></script>

      <section>
        <div className="grid-2">
          <div className="card">
            <h2>üìÑ Resume Upload</h2>
            <div
              className="resume-dropzone"
              onDrop={(e) => {
                e.preventDefault();
                if (e.dataTransfer.files.length > 0)
                  handleFile(e.dataTransfer.files[0]);
              }}
              onDragOver={(e) => e.preventDefault()}
            >
              <p>Drag & Drop or Select File (.txt or .pdf)</p>
              <input
                ref={fileInputRef}
                type="file"
                accept=".txt,application/pdf"
                style={{ display: "none" }}
                onChange={(e) => handleFile(e.target.files?.[0])}
              />
              <button onClick={() => fileInputRef.current.click()}>
                Select File
              </button>
              {fileError && <p className="error">{fileError}</p>}
            </div>
          </div>

          <div className="card">
            <h2>üìù Job Description</h2>
            <textarea
              placeholder="Paste JD here..."
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
            />
          </div>
        </div>

        {resumeText && (
          <div className="card">
            <h2>üé® Choose Template</h2>
            <div className="template-grid">
              {Object.entries(TEMPLATES).map(([key, t]) => (
                <button
                  key={key}
                  className={`template-card ${selectedTemplate === key ? "selected" : ""}`}
                  onClick={() => setSelectedTemplate(key)}
                >
                  <div className={`template-swatch ${t.className}`}></div>
                  <div>{t.name}</div>
                </button>
              ))}
            </div>
          </div>
        )}

        {analysis && (
          <div className="card">
            <h2>üìä ATS Analysis (Score: {analysis.atsScore}%)</h2>
            <ul>
              {feedback.map((f, i) => (
                <li key={i}>{f}</li>
              ))}
            </ul>
            <button onClick={downloadPDF} disabled={isUploading}>
              {isUploading ? "Uploading..." : "üìÑ Download & Upload PDF"}
            </button>
          </div>
        )}

        {/* Resume Preview */}
        {resumeText && (
          <div
            ref={previewRef}
            className={`resume-sheet ${TEMPLATES[selectedTemplate].className}`}
            style={{ fontFamily: TEMPLATES[selectedTemplate].fontStack }}
          >
            <div className="sheet-header">
              <div className="avatar">
                {candidateName.slice(0, 1).toUpperCase()}
              </div>
              <div className="headings">
                <h1 className="name">{candidateName}</h1>
                <div className="tagline">Professional Resume</div>
              </div>
            </div>
            <div
              className="sheet-body"
              dangerouslySetInnerHTML={{ __html: htmlBody }}
            />
          </div>
        )}
      </section>
    </React.Fragment>
  );
}
