sourcesContent:
import React, { useState, useEffect, useMemo } from "react";

const SECTIONS = {
  education: /\b(education|qualifications|degree|university|college)\b/i,
  skills: /\b(skills|technical skills|key skills|competencies)\b/i,
  experience: /\b(experience|work experience|employment|career)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?|licensed?)\b/i,
  contact: /\b(contact|email|phone|linkedin|github|address)\b/i,
};

const RESUME_TEMPLATES = {
  modern: {
    name: "Modern Professional",
    styles: {
      fontFamily: "Georgia, serif",
      headerBg: "#2563eb",
      textColor: "#1f2937",
      accent: "#3b82f6"
    }
  },
  minimal: {
    name: "Minimal Clean",
    styles: {
      fontFamily: "Arial, sans-serif",
      headerBg: "#374151",
      textColor: "#111827",
      accent: "#6b7280"
    }
  },
  corporate: {
    name: "Corporate Elite",
    styles: {
      fontFamily: "Times New Roman, serif",
      headerBg: "#1f2937",
      textColor: "#111827",
      accent: "#4b5563"
    }
  },
  creative: {
    name: "Creative Bold",
    styles: {
      fontFamily: "Helvetica, sans-serif",
      headerBg: "#7c3aed",
      textColor: "#1f2937",
      accent: "#8b5cf6"
    }
  }
};

const CHATBOT_RESPONSES = {
  greeting: "Hello! I'm your Resume Assistant. How can I help you improve your resume today?",
  ats: "ATS systems scan for keywords, proper formatting, and standard section headings. Make sure to include relevant keywords from the job description and use standard headings like 'Experience', 'Skills', 'Education'.",
  keywords: "Include industry-specific keywords, action verbs like 'managed', 'developed', 'implemented', and quantify your achievements with numbers and percentages.",
  format: "Use a clean, simple format with consistent fonts, clear headings, bullet points, and adequate white space. Avoid tables, graphics, and complex formatting that ATS can't read.",
  sections: "Essential sections include: Contact Information, Professional Summary, Work Experience, Skills, and Education. Optional sections: Projects, Certifications, Awards.",
  default: "I can help with ATS optimization, keyword suggestions, formatting tips, and section recommendations. What specific area would you like to improve?"
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const [showChatbot, setShowChatbot] = useState(false);
  const [showResumePreview, setShowResumePreview] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    { type: "bot", message: CHATBOT_RESPONSES.greeting }
  ]);
  const [userMessage, setUserMessage] = useState("");

  useEffect(() => {
    if (resumeText.trim()) {
      const found = {};
      const missing = [];
      
      for (const [key, regex] of Object.entries(SECTIONS)) {
        if (regex.test(resumeText)) {
          found[key] = true;
        } else {
          missing.push(key);
        }
      }
      
      const atsScore = Math.max(0, 100 - missing.length * 15);
      setAnalysis({ found, missing, atsScore });
    }
  }, [resumeText]);

  const handleFile = (file) => {
    if (!file) return;
    
    if (file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => setResumeText(e.target.result);
      reader.readAsText(file);
    } else {
      alert("Please upload a .txt file only");
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFile(e.dataTransfer.files[0]);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}`);
    }
    if (analysis.atsScore < 50) {
      fb.push("❌ Low ATS score. Add missing sections immediately.");
    } else if (analysis.atsScore < 80) {
      fb.push("⚠️ Good foundation, but can improve with optimization.");
    } else {
      fb.push("✅ Excellent resume structure! ATS-friendly.");
    }
    
    if (analysis.found.skills) fb.push("✓ Skills section detected");
    if (analysis.found.experience) fb.push("✓ Experience section found");
    if (!analysis.found.contact) fb.push("⚠️ Add clear contact information");
    
    return fb;
  }, [analysis]);

  const downloadResumePDF = () => {
    const downloadResumePDF = () => {
  if (!resumeText.trim()) return;
  // Show the in-page preview instead of opening a new window
  setShowResumePreview(true);

  // Give browser a small delay to render the preview then call print if you want to auto-open print dialog:
  // (optional) comment out the next lines if you don't want auto print
  setTimeout(() => {
    // Only print the resume preview area via print CSS injected in the preview component below
    window.print();
  }, 300);
};

    
    
    const template = RESUME_TEMPLATES[selectedTemplate];
    const printWindow = window.open("", "_blank");
    
    printWindow.document.write(`
      <html>
        <head>
          <title>Professional Resume</title>
          <style>
            * { margin: 0; padding: 0; box-sizing: border-box; }
            body {
              font-family: ${template.styles.fontFamily};
              line-height: 1.6;
              color: ${template.styles.textColor};
              background: white;
              padding: 40px;
            }
            .header {
              background: ${template.styles.headerBg};
              color: white;
              padding: 30px;
              text-align: center;
              margin-bottom: 30px;
              border-radius: 10px;
            }
            .content {
              white-space: pre-wrap;
              word-wrap: break-word;
              font-size: 14px;
              line-height: 1.8;
            }
            h1, h2, h3 { color: ${template.styles.accent}; margin: 20px 0 10px 0; }
            .template-name {
              position: absolute;
              top: 10px;
              right: 10px;
              font-size: 10px;
              opacity: 0.7;
            }
            @media print {
              body { padding: 20px; }
              .header { background: ${template.styles.headerBg} !important; -webkit-print-color-adjust: exact; }
            }
          </style>
        </head>
        <body>
          <div class="template-name">${template.name} Template</div>
          <div class="header">
            <h1>Professional Resume</h1>
            <p>Generated with Dynamic Resume Analyzer</p>
          </div>
          <div class="content">${resumeText}</div>
        </body>
      </html>
    `);
    
    printWindow.document.close();
    printWindow.focus();
    setTimeout(() => printWindow.print(), 500);
  };

// NEW: unified chat sender so quick-reply buttons can use the same logic
const sendChatMessage = (text) => {
  if (!text || !text.trim()) return;
  const newMessages = [...chatMessages, { type: "user", message: text }];

  // Simple keyword-based response (same rules you had)
  let botResponse = CHATBOT_RESPONSES.default;
  const msg = text.toLowerCase();
  if (msg.includes("ats") || msg.includes("system")) {
    botResponse = CHATBOT_RESPONSES.ats;
  } else if (msg.includes("keyword") || msg.includes("word")) {
    botResponse = CHATBOT_RESPONSES.keywords;
  } else if (msg.includes("format") || msg.includes("layout")) {
    botResponse = CHATBOT_RESPONSES.format;
  } else if (msg.includes("section") || msg.includes("part")) {
    botResponse = CHATBOT_RESPONSES.sections;
  } else if (msg.includes("hello") || msg.includes("hi")) {
    botResponse = CHATBOT_RESPONSES.greeting;
  }

  newMessages.push({ type: "bot", message: botResponse });
  setChatMessages(newMessages);
  setUserMessage("");
};

// Replace old handleChatSubmit with simple wrapper that uses sendChatMessage
const handleChatSubmit = () => {
  sendChatMessage(userMessage);
};

// Replace old handleKeyPress with this so Enter still sends
const handleKeyPress = (e) => {
  if (e.key === "Enter") {
    e.preventDefault();
    handleChatSubmit();
  }
};


  return (
    <div style={styles.app}>
      {/* Header */}
      <header style={styles.hero}>
        <div style={styles.heroContent}>
          <h1 style={styles.title}>🚀 Dynamic Resume Analyzer</h1>
          <p style={styles.subtitle}>AI-Powered ATS Optimization & Professional Templates</p>
        </div>
      </header>

      <main style={styles.container}>
        {/* Upload Section */}
        <section style={styles.uploadSection}>
          <div style={styles.card}>
            <h2 style={styles.sectionTitle}>📄 Upload Your Resume</h2>
            <div
              style={styles.dropzone}
              onDragOver={(e) => e.preventDefault()}
              onDrop={handleDrop}
            >
              <div style={styles.dropzoneContent}>
                <div style={styles.uploadIcon}>📁</div>
                <p style={styles.dropzoneText}>Drag & Drop your resume (.txt)</p>
                <span style={styles.or}>or</span>
                <input
                  type="file"
                  accept=".txt"
                  onChange={(e) => handleFile(e.target.files[0])}
                  style={styles.fileInput}
                  id="file-upload"
                />
                <label htmlFor="file-upload" style={styles.uploadButton}>
                  Choose File
                </label>
              </div>
            </div>
          </div>
        </section>

        {/* Template Selection */}
        {resumeText && (
          <section style={styles.templateSection}>
            <div style={styles.card}>
              <h2 style={styles.sectionTitle}>🎨 Choose Resume Template</h2>
              <div style={styles.templateGrid}>
                {Object.entries(RESUME_TEMPLATES).map(([key, template]) => (
                  <div
                    key={key}
                    style={{
                      ...styles.templateCard,
                      ...(selectedTemplate === key ? styles.selectedTemplate : {})
                    }}
                    onClick={() => setSelectedTemplate(key)}
                  >
                    <div
                      style={{
                        ...styles.templatePreview,
                        background: template.styles.headerBg
                      }}
                    ></div>
                    <h3 style={styles.templateName}>{template.name}</h3>
                    <p style={styles.templateDesc}>
                      {key === "modern" && "Clean and professional design"}
                      {key === "minimal" && "Simple and elegant layout"}
                      {key === "corporate" && "Traditional business format"}
                      {key === "creative" && "Bold and innovative style"}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </section>
        )}

        {/* Results Section */}
        {analysis && (
          <section style={styles.resultsSection}>
            <div style={styles.card}>
              <h2 style={styles.sectionTitle}>📊 Analysis Results</h2>
              
              {/* Score Display */}
              <div style={styles.scoreContainer}>
                <div style={styles.scoreCircle}>
                  <span style={styles.scoreValue}>{analysis.atsScore}%</span>
                  <span style={styles.scoreLabel}>ATS Score</span>
                </div>
                <div style={styles.scoreInfo}>
                  <h3>Resume Health Check</h3>
                  <p style={{
                    color: analysis.atsScore >= 80 ? "#10b981" : 
                           analysis.atsScore >= 50 ? "#f59e0b" : "#ef4444"
                  }}>
                    {analysis.atsScore >= 80 ? "Excellent!" : 
                     analysis.atsScore >= 50 ? "Good, but can improve" : "Needs improvement"}
                  </p>
                </div>
              </div>

              {/* Sections Grid */}
              <div style={styles.sectionsGrid}>
                <div style={styles.sectionCard}>
                  <h3 style={{ ...styles.cardTitle, color: "#10b981" }}>
                    ✅ Found Sections ({Object.keys(analysis.found).length})
                  </h3>
                  <ul style={styles.sectionList}>
                    {Object.keys(analysis.found).map((section) => (
                      <li key={section} style={styles.foundSection}>
                        {section.charAt(0).toUpperCase() + section.slice(1)}
                      </li>
                    ))}
                  </ul>
                </div>

                <div style={styles.sectionCard}>
                  <h3 style={{ ...styles.cardTitle, color: "#ef4444" }}>
                    ❌ Missing Sections ({analysis.missing.length})
                  </h3>
                  <ul style={styles.sectionList}>
                    {analysis.missing.map((section) => (
                      <li key={section} style={styles.missingSection}>
                        {section.charAt(0).toUpperCase() + section.slice(1)}
                      </li>
                    ))}
                  </ul>
                </div>
              </div>

              {/* Feedback */}
              <div style={styles.feedbackSection}>
                <h3 style={styles.cardTitle}>💡 Smart Recommendations</h3>
                <div style={styles.feedbackList}>
                  {feedback.map((fb, i) => (
                    <div key={i} style={styles.feedbackItem}>
                      {fb}
                    </div>
                  ))}
                </div>
              </div>

              {/* Action Buttons */}
              <div style={styles.actionButtons}>
                <button style={styles.primaryButton} onClick={downloadResumePDF}>
                  📄 Download PDF Resume
                </button>
                <button
                  style={styles.secondaryButton}
                  onClick={() => setShowChatbot(true)}
                >
                  💬 Get AI Help
                </button>
              </div>
            </div>
          </section>
        )}
      </main>

      {/* Chatbot */}
      {showChatbot && (
        <div style={styles.chatbotOverlay} onClick={(e) => {
          if (e.target === e.currentTarget) setShowChatbot(false);
        }}>
          <div style={styles.chatbot}>
            <div style={styles.chatHeader}>
              <h3>🤖 Resume Assistant</h3>
              <button
                style={styles.closeButton}
                onClick={() => setShowChatbot(false)}
              >
                ✕
              </button>
            </div>
            
            <div style={styles.chatMessages}>
              {chatMessages.map((msg, i) => (
                <div
                  key={i}
                  style={{
                    ...styles.chatMessage,
                    ...(msg.type === "user" ? styles.userMessage : styles.botMessage)
                  }}
                >
                  {msg.message}
                </div>
              ))}
            </div>
            
            <div style={styles.chatForm}>
              <input
                style={styles.chatInput}
                value={userMessage}
                onChange={(e) => setUserMessage(e.target.value)}
                placeholder="Ask about resume optimization..."
                onKeyPress={handleKeyPress}
              />
              <button style={styles.chatSend} onClick={handleChatSubmit}>Send</button>
            </div>
          </div>
        </div>
      )}
      {/* ----------------- IN-PAGE RESUME PREVIEW (no pop-up) ----------------- */}
{showResumePreview && (
  <section style={styles.previewOverlay} onClick={(e) => { if (e.target === e.currentTarget) setShowResumePreview(false); }}>
    <div id="resume-preview" style={styles.previewCard}>
      {/* Inject print-specific styling so only resume preview prints */}
      <style>{`
        /* hide everything except #resume-preview when printing */
        @media print {
          body * { visibility: hidden !important; }
          #resume-preview, #resume-preview * { visibility: visible !important; }
          #resume-preview { position: absolute; left: 0; top: 0; width: 100%; }
        }
      `}</style>

      <div style={styles.previewHeader}>
        <div>
          <strong>{RESUME_TEMPLATES[selectedTemplate].name}</strong>
          <div style={{ fontSize: 12, opacity: 0.7 }}>Preview — showing on page (no popup)</div>
        </div>
        <div>
          <button style={styles.printButton} onClick={() => window.print()}>Print</button>
          <button style={styles.closePreview} onClick={() => setShowResumePreview(false)}>Close</button>
        </div>
      </div>

      {/* Template-styled content area */}
      <div style={styles.previewContent}>
        <style>{`
          /* Inline template styles derived from selected template */
          #resume-preview .rp-body {
            font-family: ${RESUME_TEMPLATES[selectedTemplate].styles.fontFamily};
            color: ${RESUME_TEMPLATES[selectedTemplate].styles.textColor};
            line-height: 1.6;
            background: white;
            padding: 24px;
            border-radius: 8px;
          }
          #resume-preview .rp-header {
            background: ${RESUME_TEMPLATES[selectedTemplate].styles.headerBg};
            color: white;
            padding: 18px;
            border-radius: 6px;
            margin-bottom: 18px;
          }
          #resume-preview h1, #resume-preview h2, #resume-preview h3 {
            color: ${RESUME_TEMPLATES[selectedTemplate].styles.accent};
          }
        `}</style>

        <div className="rp-body" dangerouslySetInnerHTML={{ __html: resumeText.replace(/\n/g, "<br/>") }} />
      </div>
    </div>
  </section>
)}

{/* ----------------- Floating Chat Button (FAB) ----------------- */}
<button
  aria-label="Open resume assistant"
  title="Resume Assistant"
  style={styles.fab}
  onClick={() => setShowChatbot(true)}
>
  💬
</button>

{/* NOTE: Your existing chatbot overlay JSX remains valid. To add quick-default replies inside chatbot,
     replace / augment where you render chat form/messages with this small block (add inside chatbot UI): */}

{/* Quick replies block (put inside your chatbot div, above chatForm) */}
{showChatbot && (
  <div style={{ padding: "0 1rem 1rem" }}>
    <div style={{ display: "flex", gap: "0.5rem", flexWrap: "wrap", marginBottom: "0.6rem" }}>
      <button style={styles.quickBtn} onClick={() => sendChatMessage("How to improve ATS score?")}>Improve ATS</button>
      <button style={styles.quickBtn} onClick={() => sendChatMessage("Suggest keywords for software engineer")}>Keywords</button>
      <button style={styles.quickBtn} onClick={() => sendChatMessage("How should I format experience?")}>Format Experience</button>
    </div>
  </div>
)}

      {/* Footer */}
      <footer style={styles.footer}>
        <p>© 2025 Dynamic Resume Analyzer - Boost Your Career Success</p>
      </footer>
    </div>
  );
}

