import React, { useState, useEffect, useMemo } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Upload, FileText, Sparkles } from "lucide-react";
import { motion } from "framer-motion";

const SECTION = {
  education: /(education|qualifications)/i,
  skills: /(skills|technical skills|key skills)/i,
  experience: /(experience|work experience)/i,
  projects: /(projects)/i,
  certifications: /(certifications)/i,
  contact: /(contact|email|phone|linkedin|github)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
  }, [resumeText, jobDescription]);

  const handleFile = (file) => {
    if (!file || file.type !== "text/plain") {
      return alert("Only .txt format supported.");
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) => text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTION)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }
    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = splitWords(jobDescription);
      const resumeWords = splitWords(resumeText);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }
    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}.`);
    }
    if (atsScore !== null) {
      if (atsScore < 50) fb.push("⚠️ Low ATS Score – add more keywords.");
      else if (atsScore < 80) fb.push("✅ Decent ATS Score – improve further.");
      else fb.push("🎉 Excellent ATS Match! Ready to go.");
    }
    return fb;
  }, [analysis, atsScore]);

  return (
    <div
      className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-purple-100 flex flex-col items-center p-8"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      <motion.h1
        className="text-5xl font-extrabold mb-8 text-center bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <Sparkles className="inline-block w-10 h-10 mr-2 text-purple-600" />
        Dynamic Resume Analyzer
      </motion.h1>

      {/* Upload Section */}
      <Card className="w-full max-w-3xl mb-8 shadow-lg">
        <CardContent className="p-6">
          <label className="block mb-3 font-semibold text-lg flex items-center gap-2">
            <Upload className="w-5 h-5" /> Upload Resume (.txt)
          </label>
          <input
            type="file"
            accept=".txt"
            onChange={(e) => handleFile(e.target.files[0])}
            className="mb-4"
          />
          <div className="border-2 border-dashed rounded-xl p-8 text-center text-gray-500 bg-gray-50 hover:bg-gray-100 cursor-pointer transition"
            onClick={() => document.querySelector('input[type=file]').click()}
          >
            Drag & Drop resume here or Click to Upload
          </div>
        </CardContent>
      </Card>

      {/* JD Section */}
      <Card className="w-full max-w-3xl mb-8 shadow-lg">
        <CardContent className="p-6">
          <h2 className="text-xl font-semibold mb-3 flex items-center gap-2">
            <FileText className="w-5 h-5" /> Job Description
          </h2>
          <textarea
            className="w-full min-h-[250px] max-h-[400px] p-4 border rounded-xl shadow-sm resize-y focus:ring-2 focus:ring-purple-400"
            placeholder="Paste the job description here..."
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
          />
        </CardContent>
      </Card>

      {/* Analysis Results */}
      {analysis && (
        <motion.div
          className="w-full max-w-5xl bg-white p-8 rounded-2xl shadow-2xl"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.4 }}
        >
          <h2 className="text-3xl font-bold mb-6 text-indigo-700">Analysis Results</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            <div className="bg-green-50 p-4 rounded-xl border">
              <h3 className="font-semibold mb-2 text-green-700">✅ Found Sections</h3>
              <ul className="list-disc ml-6 text-green-800">
                {Object.keys(analysis.found).map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            <div className="bg-red-50 p-4 rounded-xl border">
              <h3 className="font-semibold mb-2 text-red-700">❌ Missing Sections</h3>
              <ul className="list-disc ml-6 text-red-800">
                {analysis.missing.map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            <div className="bg-blue-50 p-4 rounded-xl border flex flex-col justify-center items-center">
              <h3 className="font-semibold mb-2 text-blue-700">📊 ATS Score</h3>
              {atsScore !== null ? (
                <>
                  <Progress value={atsScore} className="w-full mb-2" />
                  <p className="text-lg font-bold">{atsScore}% Match</p>
                </>
              ) : (
                <p className="text-gray-600">Paste JD to calculate score</p>
              )}
            </div>
          </div>

          {/* Feedback */}
          <div className="bg-purple-50 p-4 rounded-xl border">
            <h3 className="font-semibold mb-2 text-purple-700">💡 Feedback</h3>
            <ul className="list-disc ml-6 text-purple-800">
              {feedback.map((f, i) => (
                <li key={i}>{f}</li>
              ))}
            </ul>
          </div>
        </motion.div>
      )}
    </div>
  );
}
