import React, { useState } from "react";
import ResumeAnalyzer from "./ResumeAnalyzer";
import Chatbot from "./Chatbot";
import Auth from "./Auth"; // New import
import "./App.css";

/**
 * Main application component. Manages authentication state and renders main content or auth screen.
 */
export default function App() {
  const [showChatbot, setShowChatbot] = useState(false);
  // State for dummy authentication
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userEmail, setUserEmail] = useState(null);

  const handleLogin = (email) => {
    setIsLoggedIn(true);
    setUserEmail(email);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserEmail(null);
    alert("Logged out. Resume data automatically cleared (DRA-SR-002 simulated).");
  };

  return (
    <div className="app">
      {/* DRA-SR-003, Webpage: Header section */}
      <header className="hero">
        <div className="hero-content">
          <div className="header-row">
            <h1 className="title-small">ðŸš€ Dynamic Resume Analyzer</h1>
            {isLoggedIn && (
              <div className="auth-info">
                <span className="user-email">{userEmail}</span>
                <button className="btn logout-btn" onClick={handleLogout}>
                  Logout
                </button>
              </div>
            )}
          </div>
          <p className="subtitle">ATS-aware scoring â€¢ Templates â€¢ Instant PDF</p>
        </div>
      </header>

      {/* Main Content conditionally rendered based on authentication */}
      <main className="container">
        {isLoggedIn ? (
          <ResumeAnalyzer />
        ) : (
          <Auth onLogin={handleLogin} />
        )}
      </main>

      {/* DRA-F-030: Floating Chatbot Button */}
      {isLoggedIn && (
        <button
          className="fab"
          aria-label="Open Resume Assistant"
          onClick={() => setShowChatbot(true)}
        >
          ðŸ’¬
        </button>
      )}

      {/* DRA-F-030: Chatbot Modal */}
      {isLoggedIn && showChatbot && <Chatbot onClose={() => setShowChatbot(false)} />}

      {/* Webpage: Footer section */}
      <footer className="footer">
        <p>Â© 2025 Dynamic Resume Analyzer | Developed by Agile Team (DRA-NF-004: Logs, DRA-NF-005: Accessibility supported)</p>
      </footer>
    </div>
  );
}
